  -- Azogs Blut / Azogs Reich
  [1879415681] = {
      [1] = {
      ["NAME"] = "Kamaz-kala"; 
      ["PROGRESS"] = "Erfahrt etwas über Kamaz-kala."; 
      ["LORE"] = "'Die Orks hatten eine primitive Grenzfestung entlang des alten Langen Wegs errichtet und uns den sichersten Weg zum Osttor verstellt.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "45.67N"; 
          ["LONG"] = "123.93W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Gadh Ujáf"; 
      ["PROGRESS"] = "Erfahrt etwas über Gadh Ujáf."; 
      ["LORE"] = "'In den Hügeln von Zirakzigil stieß ich auf einen großen Wargbau. Bevor Azogs jüngster Sohn Komog bei Gundabad den Tod fand, hatten seine Warge unzählige Zwerge getötet. Endlich würden wir uns rächen können!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "65.22N"; 
          ["LONG"] = "135.42W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Dursh Hûl"; 
      ["PROGRESS"] = "Erfahrt etwas über Dursh Hûl."; 
      ["LORE"] = "'Nördlich von Khur Azan fanden wir die alte Brücke zerstört vor. An ihrer Stelle hatten die Orks einen kleinen Übergang errichtet, um den Pass nach Shathûr zu schützen.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "65.87N"; 
          ["LONG"] = "132.67W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Târ-kútoz"; 
      ["PROGRESS"] = "Erfahrt etwas über Târ-kútoz."; 
      ["LORE"] = "'Im Schatten von Atrad-zarakh, auf den Hängen von Shathûr, hatten die Orks einen riesigen Wachposten gebaut. Zwischen ihren primitiven Zeichen hatten unzählige Fledermäuse Unterschlupf gesucht - Kreaturen, die Azog und seine Söhne schon lange mit Vorliebe für das Ausspionieren von Azanulbizar eingesetzt hatten.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.80N"; 
          ["LONG"] = "134.65W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Rung-shakam"; 
      ["PROGRESS"] = "Erfahrt etwas über Rung-shakam."; 
      ["LORE"] = "'Weit über dem Osttor, auf den Hängen von Zirakzigil, schlugen die Orks mit Trollen aus Moria ein Lager auf. Aus dieser schwindelerregenden Höhe warfen die Trolle unzählige Steine hinab ins Tal und töteten Orks und Zwerge gleichermaßen.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "68.12N"; 
          ["LONG"] = "137.67W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Zaghúth"; 
      ["PROGRESS"] = "Erfahrt etwas über Zaghúth."; 
      ["LORE"] = "'Als wir schließlich nach Azanulbizar kamen, war der Pass durch Baraz durch ein großes Lager der Orks blockiert. Von Norden würde keine Verstärkung kommen - zumindest keine, die wir als Freunde und Verbündete betrachten würden.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "70.88N"; 
          ["LONG"] = "137.32W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Muru Azog"; 
      ["PROGRESS"] = "Erfahrt etwas über Muru Azog."; 
      ["LORE"] = "'Vor den Toren von Moria errichteten Azog und seine Orks eine große Verteidigungsanlage. Im Schatten seiner Wachtürme waren unzählige Zwergenschädel aufgespießt worden, um Thráin zu verhöhnen.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "67.52N"; 
          ["LONG"] = "136.58W"; 
        }; 
      }; 
      };
