  -- Gundabad / Auskundschaften des Kummertals
  [1879422516] = {
      [1] = {
      ["NAME"] = "Leitstáth"; 
      ["PROGRESS"] = "Erkundet Leitstáth."; 
      ["LORE"] = "Nach ihrem Sieg über die Truppen der Eisernen Krone auf dem Rathad Caul, dem engen Weg, errichtete eine von Prinz Ingór befehligte Zhélruka-Expedition das Lager Leitstáth in Câr Bronach. Nach der Gefangennahme von Grúmachath dient Leitstáth inzwischen jedoch hauptsächlich als Basis und Versorgungsposten für die Gabil'akkâ."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "46.97N"; 
          ["LONG"] = "64.95W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Crágan Bris"; 
      ["PROGRESS"] = "Erkundet Crágan Bris."; 
      ["LORE"] = "Crágan Bris, das zerstört wurde, als der Hexenkönig von Angmar versuchte, Câr Bronach zu unterwerfen, war einst ein Dorf der Steinriesen der Grímklettar-Sippe. Nachdem sie von den Dienern Angmars aus ihrer angestammten Heimat vertrieben worden waren, wagte sich jahrelang niemand, es zu betreten. Daher sind inzwischen nur noch Steintrümmer von ihm übrig."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "49.54N"; 
          ["LONG"] = "65.50W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Grúmachath"; 
      ["PROGRESS"] = "Erkundet Grúmachath."; 
      ["LORE"] = "Grúmachath wurde vor langer Zeit errichtet, als das Königreich Angmar noch weit in den Osten reichte und stellte einen befestigten Außenposten der Eisernen Krone dar. Nachdem es vor Kurzem von der Zhélruka-Expedition des Prinzen Ingórs eingenommen wurde, dient Grúmachath nun als Hauptlager der Zwerge der Gabil'akkâ in Câr Bronach bei deren Bestrebungen, den Gundabad zurückzuerobern."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "50.15N"; 
          ["LONG"] = "68.54W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Eiglinn"; 
      ["PROGRESS"] = "Erkundet Eiglinn."; 
      ["LORE"] = "Der Eiglinn war einst ein funkelnder See auf dem Westgipfel der Ered Mithrin, wurde inzwischen jedoch von den Angmarim der Eisernen Krone geschändet. Als unerwartetes Nebenprodukt der bösen Magie und finsteren Experimente in Tûr Fúar, der Unheilsspitze, ist herbes, blaugrünes Abwasser in das Grundwasser gedrungen und verseucht den Eiglinn sowie weitere Gewässer in Câr Bronach."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "49.73N"; 
          ["LONG"] = "70.61W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Clavanail"; 
      ["PROGRESS"] = "Erkundet Clavanail."; 
      ["LORE"] = "Nachdem sie nach der Flucht der Eisernen Krone aus Angmar aus ihrer Heimat vertrieben worden waren, nisten nun zahlreiche Vögel aus Câr Bronach in den Felsklippen von Clavanail, abgesehen natürlich von den Aasfressern, die sich nun an den Überresten der Opfer der Eisernen Krone laben."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.19N"; 
          ["LONG"] = "72.14W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Eiricháv"; 
      ["PROGRESS"] = "Erkundet Eiricháv."; 
      ["LORE"] = "Nach dem Fall Angmars kam es unter den Bergmenschen der Trév Duvárdain zum Streit. Obwohl viele von ihnen immer noch der Eisernen Krone dienen, hat eine wachsende Zahl der Bergmenschen ihre bösen Geschwister verlassen und Zuflucht in der Eiricháv gesucht. Ob sie nach Angmar zurückkehren und sich wieder mit den Trév Duvárdain vereinen werden, ist zwar ungewiss, aber in der Eiricháv führen sie ein freies Leben ohne Unterdrücker - und das reicht ihnen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.08N"; 
          ["LONG"] = "71.40W"; 
        }; 
      }; 
      };
  };
  -- Gundabad / Angmars letzte Front
  [1879422517] = {
      [1] = {
      ["NAME"] = "Rathad Caul, den Engen Pfad."; 
      ["PROGRESS"] = "Erkundet Rathad Caul, den Engen Pfad."; 
      ["LORE"] = "Zwischen dem Ältestental und Câr Bronach liegt ein befestigter Pass, der als Rathad Caul oder enger Weg bekannt ist. Schroffe Felsen ragen entlang des Rathad Cauls auf, der nach mehreren blutigen Schlachten Angmars gegen die Zhélruka durchbrochen und verlassen wurde. Da er zur Zeit des Hexenkönigs einen Teil der langen Grenze Angmars darstellte, stellt der Rathad Caul auch den westlichsten Punkt von Men Angrún dar, einer alten Straße, die sich von Angmar nach Rhûn erstreckt, das einst aus Câr Bronach entstand."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "44.57N"; 
          ["LONG"] = "63.88W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Hlídborg"; 
      ["PROGRESS"] = "Erkundet Hlídborg."; 
      ["LORE"] = "Nachdem die Steinriesen der Grímklettar-Sippe vom Hexenkönig von Angmar aus ihrer angestammten Heimat in Tind Brotna vertrieben und die Siedlung Crágan Bris zerstört worden war, zogen sie sich durch die Täler Câr Bronachs zurück. Auf einem schroffen Südhang gründeten die Grímklettar eine neue Siedlung: Hlídborg. Nach jahrhundertelangen Entbehrungen haben einige Grímklettar sich dazu herabgelassen, mit den Angmarim der Eisernen Krone gemeinsame Sache zu machen, um ihren einstigen Reichtum zurückzuerlangen, wodurch die Überreste der Sippe jedoch weiter zersplitterten."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "48.95N"; 
          ["LONG"] = "63.68W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Skáilag"; 
      ["PROGRESS"] = "Erkundet Skáilag."; 
      ["LORE"] = "Als das Reich Angmar auf dem Gipfel seine Macht war, kämpften und starben böse Bergmenschen aus Rhudaur in den Diensten des Hexenkönigs. Die Gefallenen wurden in weitläufigen Hügelgräbern begraben, die man in die schroffen Talwände von Câr Bronach trieb. Viele dieser Gräber sind zwar inzwischen verloren gegangen, aber eines steht noch: Skáilag."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "50.89N"; 
          ["LONG"] = "65.88W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tâg Fógarrach"; 
      ["PROGRESS"] = "Erkundet Tâg Fógarrach."; 
      ["LORE"] = "Ihrem Treueschwur gegenüber der Eisernen Krone zum Trotz zersplitterten die Bergmenschen von Trév Duvárdain nach der gescheiterten Wiedergeburt Angmars. Viele flohen aus Carn Dûm und suchten in den Hügeln Angmars Zuflucht oder baten die Trév Gállorg um Gnade, um sich in Aughaire ansiedeln zu können. Doch einige Trév Duvárdain blieben treue Diener der Eisernen Krone und folgten ihren besiegten Herren in der Hoffnung nach Câr Bronach, doch noch den versprochenen Ruhm erlangen zu können. In Tâg Fógarrach schlugen die Trév Duvárdain in den Ruinen einer alten Angmarim-Siedlung ihr Lager auf, scheinbar unbekümmert vom Schicksal der Menschen, die dort vor ihnen gelebt hatten."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "51.33N"; 
          ["LONG"] = "71.61W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Toll Oivas"; 
      ["PROGRESS"] = "Erkundet Toll Oivas."; 
      ["LORE"] = "Toll Oivas war einst lediglich eine gewundene Höhle, in der die Fledermäuse des Gundabad hausten. Doch nach der Rückkehr der Angmarim nach Câr Bronach ist sie ein Ort schrecklicher Magie geworden. Entschlossen herauszufinden, was die Orks, Frosthorde und Gabil'akkâ vorhaben, haben die Angmarim böse Geister an die Überreste der erschlagenen Fledermäuse gebunden, um sie als geflügelte Spione inner- und außerhalb des Gundabad einzusetzen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "51.79N"; 
          ["LONG"] = "74.91W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Mur Shatraug, das Hexentor"; 
      ["PROGRESS"] = "Erkundet Mur Shatraug, das Hexentor."; 
      ["LORE"] = "Mur Shatraug, das sogenannte Hexentor, welches einst das Osttor des Gundabad war, führt von den Höhen des Steinschlundlochs nach Câr Bronach. Lange nach dem Fall des Gundabad und dessen Besetzung durch die Orks wurde das Osttor vom Schreckensreich Angmar eingenommen und zu einer kleinen Siedlung ausgebaut. Obwohl Angmar in den folgenden Jahren unterging, stellt das Mur Shatraug noch immer ein Monument der Macht dar, die der Hexenkönig von Angmar einst in Mittelerde besaß."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "48.42N"; 
          ["LONG"] = "70.62W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Agal Dûn"; 
      ["PROGRESS"] = "Erkundet Agal Dûn."; 
      ["LORE"] = "Agal Dûn, die Festung des Schreckens, wurde zu Angmars Hochzeit errichtet und wacht schon lange über Câr Bronach. Sie ist manchen lediglich unter dem Namen ihres zerstörten Turmes Tûr Fúar, der Unheilsspitze, bekannt und war die östlichste Siedlung Angmars, bevor sie nach mehreren Schlachten zwischen den Zhélruka und den Truppen des Hexenkönigs aufgegeben wurde, die den Niedergang des Reiches einleiteten. In den folgenden Jahrhunderten schlummerte sie vor sich hin und geriet größtenteils in Vergessenheit, bis der Falsche König von Angmar, Mordirith, abgesetzt wurde und die Angmarim der Eisernen Krone nach der gescheiterten Wiedererrichtung ihres Reiches zur Flucht gezwungen wurden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "52.58N"; 
          ["LONG"] = "69.29W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Tûr Fúar, die Unheilsspitze"; 
      ["PROGRESS"] = "Erkundet Tûr Fúar, die Unheilsspitze."; 
      ["LORE"] = "Tûr Fúar, die Unheilsspitze, die in lange zurückliegenden Schlachten zwischen Angmar und den Zhélruka geschleift wurde, thront über Câr Bronach wie ein gekrümmtes Klauenskelett. Auf dem Gipfel von Angmars Macht bewahrte der Hexenkönig von Angmar dort angeblich ein mächtiges Artefakt auf. Nach dem Niedergang Angmars blieb Tûr Fúar jedoch über eintausend Jahre leer. Während seines langen Schlafes machten die Orks einen großen Bogen um sie, da sie sie für verflucht hielten und benannten sie in Shalgaush um: den Knochenfinger. Selbst während der gescheiterten Wiedergeburt Angmar erhoben weder der falsche König von Angmar noch seine Diener Anspruch auf die Unheilsspitze. Nachdem sie besiegt und aus Angmar vertrieben wurden, haben die Überreste der Eisernen Krone in Tûr Fúar Zuflucht gesucht und suchen verzweifelt nach einem Weg, wieder an die Macht zu gelangen und das Reich Angmar zu einstigem Ruhm zurückzuführen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "57.72N"; 
          ["LONG"] = "10.21W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Bálach Iaran, der Eisenpass"; 
      ["PROGRESS"] = "Erkundet Bálach Iaran, den Eisenpass."; 
      ["LORE"] = "Bálach Iaran, der Eisenpass, windet sich durch das Gebirge Nan Gurth. Er lag nach dem Fall Angmars viele Jahre lang brach. Nach der Niederlage des falschen Königs von Angmar bei Barad Gúlaran nutzte ihn die Eiserne Krone jedoch, um nach Câr Bronach zu fliehen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "54.41N"; 
          ["LONG"] = "72.94W"; 
        }; 
      }; 
      };
  };
