  -- Gundabad / Durins Wiege
  [1879422591] = {
      [1] = {
      ["NAME"] = "Zâram-mekhem"; 
      ["PROGRESS"] = "Erkundet Zâram-mekhem."; 
      ["LORE"] = "Bedeckt von düsteren Warnbotschaften in Khuzdul besteht Zâram-mekhem, das Seetor, aus zwei großen mit Runen verzierten Steinsäulen an der Grenze von Tiefengier und Düsterting. Einst lag ein mächtiger Zauber darauf, der lediglich die Zwerge Mittelerdes in die heiligen Höhlen des Gundabad einließ. In jüngster Zeit jedoch wurde er von den Angmarim mithilfe eines vor langer Zeit vom Hexenkönig von Angmar angefertigten Artefakts überwunden: Máltagar, dem Grausamen Totengräber."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "50.43S"; 
          ["LONG"] = "120.47W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Durthuzâram, der Schwarze See"; 
      ["PROGRESS"] = "Erkundet Durthuzâram, den Schwarzen See."; 
      ["LORE"] = "Durthuzâram, der Schwarze See, ist eines der Naturwunder des Gundabad und Mittelerdes. Der tiefe, klare und stille Durthuzâram, der Düsterting, gab den heiligen Hallen des Gundabad ihren Namen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "46.28S"; 
          ["LONG"] = "120.85W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Wächterquartier"; 
      ["PROGRESS"] = "Erkundet das Wächterquartier."; 
      ["LORE"] = "Das Wächterquartier, von dem die Langbärte einst fast den gesamten Durthuzâram überblicken. Inzwischen dient es den Gabil'akkâ als provisorisches Lager, um ihre Gegner aus den heiligen Höhlen von Düsterting fernzuhalten."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "47.72S"; 
          ["LONG"] = "121.91W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Lugun-nâla"; 
      ["PROGRESS"] = "Erkundet Lugun-nâla."; 
      ["LORE"] = "Lugun-nâla, der heilige Weg, ist eine alte Brücke, die aus dem Basalt von Düsterting geschlagen wurde. Sie erstreckt sich über den Durthuzâram und führt zur vielleicht heiligsten Stätte ganz von Gundabad: Abkân'aban, der Stein des Erwachens."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "45.40S"; 
          ["LONG"] = "122.12W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Abkân'aban, der Stein des Erwachens"; 
      ["PROGRESS"] = "Erkundet Abkân'aban, den Stein des Erwachens."; 
      ["LORE"] = "Der Legende nach erwachte Durin der Unsterbliche erstmalig beim Abkân'aban, dem Stein des Erwachens, in den Tiefen des Gundabad. Auf seiner irdenen Wiege solle er angeblich einen Amboss aus schwarzem Stein, den Narag-dehar, und eine Esse aus weißen Ziegeln, die Labam-khebab, aufgestellt haben. Nach dem Aufstand der Grauhämmer wurde jedoch verfügt, dass die Esse bis zur prophezeiten Rückkehr Durins des Letzten nicht mehr benutzt werden sollte. So wurde das Feuer darin gelöscht und sie diente danach nur noch den Langbärten als Zeremonienstätte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "44.99S"; 
          ["LONG"] = "120.87W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Burmumekhem"; 
      ["PROGRESS"] = "Erkundet Burmumekhem."; 
      ["LORE"] = "Bedeckt von düsteren Warnbotschaften in Khuzdul besteht Burmumekhem, das Klufttor, aus zwei großen mit Runen verzierten Steinsäulen an der Grenze der Spaltkluft zum Düsterting. Einst lag ein mächtiger Zauber darauf, der lediglich die Zwerge Mittelerdes in die heiligen Höhlen des Gundabad einließ. Doch er wurde ebenfalls von den Angmarim der Eisernen Krone überwunden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "42.63S"; 
          ["LONG"] = "121.90W"; 
        }; 
      }; 
      };
  };
