  [1879422593] = {
      [1] = {
      ["NAME"] = "Drúkala"; 
      ["PROGRESS"] = "Erkundet Drúkala."; 
      ["LORE"] = "Das den Orks lange als Skaizagâf oder auch Fluchhöhle bekannte Düsterting war für die meisten ihrer Art tabu, bis die Angmarim die Schutzrunen mithilfe Máltagars, des Grausamen Totengräbers, überwanden. Obwohl die Orks Düsterting nun betreten können, ohne Angst vor dem Zauber der Schutzrunen haben zu müssen, verweilen die meisten dort nicht lange, da die Echos in der Dunkelheit ein Eigenleben zu besitzen scheinen. Die wenigen Orks, die es wagen, die heilige Höhle zu erkunden, haben ihr Lager in Drúkala, unweit der ersten Steine an der Grenze von Tiefengier und Düsterting aufgeschlagen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "50.20S"; 
          ["LONG"] = "121.29W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Ormgren"; 
      ["PROGRESS"] = "Erkundet Ormgren."; 
      ["LORE"] = "Obwohl von ihren einst reichen Vorkommen praktisch nichts mehr übrig ist, war Ormgren eine der ersten Adern, die Durin der Unsterbliche nach seinem Erwachen im Gundabad erforschte. Viele Jahre nach der Abriegelung von Düsterting bewohnten einzig Glühwürmchen die alten Tunnel. Doch seit der Ankunft der Angmarim sind viele davon ungewöhnlich aggressiv geworden. Anderen haben sich hingegen tiefer nach Ormgren zurückgezogen und erleuchten mit ihrem blassen Schein die langvergessenen Kammern des Gundabad."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "44.65N"; 
          ["LONG"] = "11.60E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Glittengrof"; 
      ["PROGRESS"] = "Erkundet Glittengrof."; 
      ["LORE"] = "Die Höhle Glittengrof, wo Durin der Unsterbliche angeblich die ersten Kristalle fand, mit denen er die Hallen des Gundabad erleuchtete, beherbergt inzwischen eine Spinnenbrut, deren Netze drohen, den gesamten Düsterting zu schänden."; 
      ["POINTS"] = {
