      [5] = {
      ["NAME"] = "Mîn Lorg"; 
      ["PROGRESS"] = "Erkundet Mîn Lorg."; 
      ["LORE"] = "Entschlossen, die Geheimnisse des Düsterting zu lüften, haben die Angmarim versehentlich Mîn Lorg entdeckt, einen alten, scheinbar unberührten Durchgang, der in die tiefsten Tiefen des Gundabad führt."; 
      ["POINTS"] = {
