  -- Die Bergfestung / Abnankâra, der Heimlichhort - Entdecker des Heimlichhorts
  [1879443581] = {
      [1] = {
      ["NAME"] = "Jókulskeld"; 
      ["PROGRESS"] = "Entdeckt Jókulskeld."; 
      ["LORE"] = "Jókulskeld gilt als Quelle des Flusses Kurdushâlak, der durch die Spaltkluft fließt, und erstarrte durch den Atem von Hrímil Frostherz zu festem Eis. Hoch über diesem Eisquell führen verfallene uralte Wege nach Abnankâra, dem Heimlichhort."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "49.99N"; 
          ["LONG"] = "45.31E"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Abnankâra, der Heimlichhort"; 
      ["PROGRESS"] = "Entdeckt Abnankâra, der Heimlichhort"; 
      ["LORE"] = "Tief in den Höhlen der Spaltkluft liegt Abnankâra, der Heimlichhort. Er wurde in der Altvorderenzeit zur Zeit der Rebellion gegen die Langbärte von Mótsog und den Grauhämmern heimlich aus den Felsen gehauen und war als große Schatzkammer gedacht - das Juwel eines neuen Reiches im Gundabad. Nach der Niederlage von Mótsog und den Grauhämmern in der Entzweiungsschlacht wurde Abnankâra aufgegeben und geriet bis zu seiner Wiederentdeckung im späten Dritten Zeitalter in Vergessenheit."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "51.07N"; 
          ["LONG"] = "45.31E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Ibkêrmazal"; 
      ["PROGRESS"] = "Entdeckt Ibkêrmazal."; 
      ["LORE"] = "In Abnankâra, dem Heimlichhort, diente Ibkêrmazal als große Waffenkammer und zugleich als Bollwerk gegen jene, die die Schätze der Grauhämmer plündern wollten. Auf Mótsogs Befehl wurde die Halle in drei Abschnitte geteilt - jeweils flankiert von Speerfallen und verborgenen Kasernen -, um Eindringlinge voneinander zu trennen und zu überwältigen und in Zeiten der höchsten Not die weichen Bäuche der Drachen zu durchbohren."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "48.13N"; 
          ["LONG"] = "46.90E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Akrazulgibshîn"; 
      ["PROGRESS"] = "Entdeckt Akrazulgibshîn."; 
      ["LORE"] = "Auch wenn Akrazulgibshîn niemals ganz an die Verheißung der großen Schatzkammer heranreichen sollte, die Mótsog sich so ersehnte, wurde sie zu einer Lagerstätte all der Reichtümer von Gundabad, die die Grauhämmer erbeuteten, nachdem Durin viele der Langbärte nach Khazad-dûm geführt hatte. Nach der Entzweiungsschlacht und dem verheerenden Ende der Grauhammer-Rebellion legte Durin den Kriegshammer von Mótsogs letztem Sohn Módvit mit zu den Schätzen und ließ Abnankâra, den Heimlichhort für immer hinter sich."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "49.45N"; 
          ["LONG"] = "46.91E"; 
        }; 
      }; 
      };
  };
