  -- Trollhöhen / Orte des Bogens
  [1879443654] = {
      [1] = {
      ["NAME"] = "Adlerfarnhütte"; 
      ["PROGRESS"] = "Erkundet die Adlerfarnhütte."; 
      ["LORE"] = "Die Adlerfarnhütte ist das friedliche und idyllische Heim von Maddock und Glenys Hirschhorn, einer Holzfällerfamilie, die vor einigen Jahren in den Bogen kam. Sie leben vom Fällen der kräftigen Bäume von Mitheithel und vom Verkauf der kleinen Schnitzereien, die Glenys anfertigt. Ihr Sohn Bran erkundet die nahegelegenen Wälder und gerät dabei öfters in Schwierigkeiten, aus denen er sich bislang aber immer wieder herauswinden konnte, wie sein Vater mit einigem Stolz jedem gegenüber erwähnt, der ihm über den Weg läuft."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "39.40S"; 
          ["LONG"] = "17.38W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Tham Lumren"; 
      ["PROGRESS"] = "Erkundet Tham Lumren."; 
      ["LORE"] = "Die Bäume, die einst Tham Lumren, die schattige Halle, behüteten und beschützten, gingen vor langer Zeit ein, im Krieg zwischen den Elben und Sauron, und wurden nie mehr neu gepflanzt. Und doch sind die Elben zu diesen Ruinen zurückgekehrt, und wer weiß, vielleicht pflanzen sie die Bäume wieder, ehe sie fortgehen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "38.68S"; 
          ["LONG"] = "17.33W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Gaerond"; 
      ["PROGRESS"] = "Erkundet Gaerond."; 
      ["LORE"] = "In den leeren Ruinen von Gaerond sind jetzt Kundschafter untergekommen, die die Söhne Elronds von Zeit zu Zeit aussenden, um den Bogen von Mitheithel im Auge zu behalten. Sie sind nicht die einzigen Wanderer, die in letzter Zeit von den Trollhöhen kommen: Die Sicherheit des Landes wird seit einiger Zeit von Trollen, Räubern und Kreaturen mit gefährlicheren Absichten bedroht."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "34.73S"; 
          ["LONG"] = "22.12W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Zuflucht der Cuan-lûth"; 
      ["PROGRESS"] = "Erkundet die Zuflucht der Cuan-lûth."; 
      ["LORE"] = "In dieser Höhle, die vormals den Waldläufern von Tornhad eine Zuflucht bot, sind jetzt Flüchtlinge der Cuan-lûth untergekommen, die sich weigern, Saruman zu folgen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "41.79S"; 
          ["LONG"] = "23.39W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Tornhad"; 
      ["PROGRESS"] = "Erkundet Tornhad."; 
      ["LORE"] = "Das Dorf Tornhad, das im Bogen unweit von Thelgarth am Grund einer bewaldeten Schlucht an einem Fluss liegt, ist die bedeutendste Waldläufersiedlung in der Gegend. Anders als mehrere andere Ansiedlungen der Dúnedain konnte Tornhad sich als mehr oder weniger permanente Siedlung dieses Volkes halten, in erster Linie dank ihrer Fähigkeit, sich bei der Gefahr einer Entdeckung in Höhlen in den Bergen zu flüchten.\n\nIn Friedenszeiten leben viele Waldläuferfamilien in Tornhad und bringen den Kindern schon von klein auf ihre Überlebenskünste bei. Manche Kinder werden zudem in Bruchtal in Geschichte, Kunst und Handwerk unterrichtet. Angesichts der drohenden Kriegsgefahr haben sich viele der Dúnadan-Frauen und -Kinder schon in die Sicherheit ihrer verborgenen Höhlen gerettet, während andere sich zum Krieg rüsten, die Wildnis durchstreifen und auf Aragorns Geheiß die Straßen frei halten."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "42.99S"; 
          ["LONG"] = "23.51W"; 
        }; 
      }; 
      };
  };
  -- Trollhöhen / Gegner des Bogens
  [1879443655] = {
      [1] = {
      ["NAME"] = "Eitheldir"; 
      ["PROGRESS"] = "Erkundet Eitheldir."; 
      ["LORE"] = "Die Festung Eitheldir, die Brunnenwache, wurde im Zweiten Zeitalter oberhalb der Kreuzung von Weißquell errichtet, um die Passage nach Lindon zu sichern. Im Krieg zwischen den Elben und Sauron wurde die Festung jedoch zum Schauplatz einer schmerzlichen Niederlage, als Hauptmann Oranoril die Armeen Saurons furchtlos in die Festung lockte, wo sie und ihre Gefährten den Tod fanden.\n\nDoch Oranorils Opfer gilt nicht als Niederlage, denn dank ihr und ihren auserwählten Kriegern gelang vielen der Eldar die Flucht zu dem neu gegründeten Zufluchtsort Imladris im Nebelgebirge, wo sie den schändlichen Taten Saurons entrinnen und sich zum Gegenangriff gruppieren konnten, der letztendlich zu seinem vorübergehenden Sturz führte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "36.19S"; 
          ["LONG"] = "22.61W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Glynafon"; 
      ["PROGRESS"] = "Erkundet Glynafon."; 
      ["LORE"] = "Als tausend Jahre des Dritten Zeitalters ins Land gegangen waren, überquerte ein Stamm der Hól-budlan, die sich Stôrs nannten, die Berge auf der Suche nach einem gastfreundlichen Ort, um sesshaft zu werden. Sie gründeten die Kolonie Glynafon, in der eine Reihe florierender Siedlungen zwischen den Felsen und Bäumen des Bogens entstand.\n\nEtwa dreihundert Jahre lebten die Stôrs friedlich in Glynafon, doch durch die Unruhen in Rhudaur und die wachsende Bedrohung durch Angmar, dem jungen Reich des Hexenkönigs, waren sie gezwungen, den Bogen auf der Suche nach neuen Ländereien zu verlassen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "39.97S"; 
          ["LONG"] = "18.37W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Feste Waeren"; 
      ["PROGRESS"] = "Erkundet die Feste Waeren."; 
      ["LORE"] = "Die Wälder der Trollhöhen und des Bogens sind mit den Ruinen des Teilreiches Rhudaur übersät. Die Provinz wurde sowohl von den Trollen der Trollhöhen als auch von den Amonedain heimgesucht, die vor der Ankunft der Dúnedain im Bogen lebten. Mit der Zeit ging Rhudaur durch den Druck von außen und den Verrat von innen zugrunde und der Bogen wurde zu Grenzland. Manche kannten diese Grenze als Harnaith, den südlichen Punkt, andere als unbezähmbare Wildnis, die man besser den Trollen und Hügelmenschen überließ.\n\nWelche finsteren Taten auf Geheiß der Diener des Hexenkönigs in der Feste Waeren begangen wurden, haben die Menschen zum Glück vergessen, doch die wachsamen Steine und die blutgetränkte Erde wissen es noch heute."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "38.39S"; 
          ["LONG"] = "22.32W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Thelgarth"; 
      ["PROGRESS"] = "Erkundet Thelgarth."; 
      ["LORE"] = "Die Festung Thelgarth war gerade im Bau befindlich, als Sauron Gondor angriff und damit den Krieg des Letzten Bündnisses auslöste. Als die Überlebenden einige Jahre später zurückkehrten, wurde den Amonedain, die an der Seite der Freien Völker kämpften, der Verbleib im Bogen gestattet. Sie halfen bei der Vollendung des Baus von Thelgarth, einem Bollwerk gegen einen Angriff von Süden, der niemals kam.\n\nStattdessen drohte Gefahr von Norden, als das Königreich Arnor in die drei Teilreiche Arthedain, Cardolan und Rhudaur zerfiel. Als dieses letzte unter den Einfluss des Hexenkönigs geriet, hielt die Zitadelle Thelgarth am längsten stand und diente trotz der wachsenden Finsternis von Angmar als Sitz der überlebenden Dúnadan-Könige. Als der König von Rhudaur und sein ganzer Haushalt mit einer Ausnahme auf Befehl des Hexenkönigs ermordet wurden, suchte die letzte Prinzessin von Rhudaur heimlich Zuflucht in Thelgarth.\n\nDoch Seuchen und die wachsende Macht Angmars verhießen nichts Gutes für die Dúnedain. 1637 überrannten Angmarim-Kultisten die Mauern von Thelgarth und ermordeten seine Verteidiger bei einem Massaker, das als die Nacht der Roten Wasser in die Geschichte einging. Danach blieb der Bogen jahrhundertelang menschenleer, doch mit der Zeit schlichen sich Halborks und Räuber in die verlassene Ruine Thelgarth, ohne etwas von seiner langen und bewegten Geschichte zu ahnen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "44.54S"; 
          ["LONG"] = "22.90W"; 
        }; 
      }; 
      };
  };
