  -- Schwanenfleet & Cardolan / Die vielen Völker des Glanduin
  [1879450859] = {
      [1] = {
      ["NAME"] = "Mooshang"; 
      ["PROGRESS"] = "Findet Mooshang."; 
      ["LORE"] = "Das kleine Grenzdorf Mooshang liegt unweit der zerstörten Brücken des überfluteten Tharbad, aber falls irgendeiner seiner Bewohner von den stolzen Händlern der Grauflut abstammen sollte, ist dies längst in Vergessenheit geraten. Vergessen sind auch die Gefahren der Vergangenheit: Mooshang war viele Jahre lang ein Ort des Friedens, hinter dessen Palisaden sich ehemalige Abenteurer neben ärmlichen Bauern und Handwerkern zur Ruhe gesetzt haben. Durch seine Gassen rennen vorwitzige Kinder, machen Unfug und lehnen sich gegen die Vorschriften und Verbote ihrer Eltern auf.\n\nEin kürzlicher Überfall der Uruk-hai mit dem Zeichen der Weißen Hand hat ein Feuer der Besorgnis unter den Bewohnern von Mooshang entzündet und ihren Tatendrang entfacht."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "60.66S"; 
          ["LONG"] = "34.59W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Clegur"; 
      ["PROGRESS"] = "Findet Clegur."; 
      ["LORE"] = "Das ruhige, friedliche Dorf Clegur ist die Heimat eines kernigen Hobbitstamms, der früher in großer Zahl das ganze Gebiet bewohnte. Unter Bürgermeister Malmadog Torlans wachsamen Blick jagen die hier lebenden Starren die wilden Tiere der Täler, bleiben ansonsten aber unter sich. Solange sie ihre Vorratskammern mit Pökelfleisch und genug Wurzelgemüse für den Winter füllen können, wird es den Bewohnern von Clegur auch weiterhin gut gehen.\n\nLaut seinen Einwohnern ist Clegur das beste der drei Dörfer in den Starrentälern."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.74S"; 
          ["LONG"] = "29.18W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Lhan Garan"; 
      ["PROGRESS"] = "Findet Lhan Garan."; 
      ["LORE"] = "Die Siedlung Lhan Garan wurde erst kürzlich am Rand des Sumpflands des Watwassers von Dunländern errichtet, die nach Norden kamen, um ein besseres Leben als das zu suchen, das sie in Enedwaith und Dunland befürchteten. Sie wünschen sich ein friedliches Nebeneinander mit den Leuten von Mooshang auf der anderen Seite der Alten Südstraße, und bislang lief dieses auch ohne ernsthafte Zwischenfälle ab. Mit einer Ausnahme: Die Kinder von Mooshang interessieren sich brennend für die Gefahren, die gleich unter der Oberfläche des Sumpflands lauern, und die Kundschafter von Lhan Garan mussten schon einige von ihnen retten und zurück nach Hause schicken. Die Dunländer sind darüber weitaus amüsierter als die Leute von Mooshang."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "54.96S"; 
          ["LONG"] = "26.99W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Glyn Helyg"; 
      ["PROGRESS"] = "Findet Glyn Helyg."; 
      ["LORE"] = "Das Dorf Glyn Helyg sticht unter den anderen Siedlungen der Starrentäler heraus, da es an der Klippe einer schmalen Schlucht mit Ausblick auf einen tosenden Wasserfall erbaut wurde. Es bleibt zu hoffen, dass die vielen Brücken, die zu den verschiedenen Ebenen des Dorfs führen, stabiler sind, als sie aussehen. Oder vielleicht wurden sie in erster Linie für Leute mit Hobbitstatur gebaut?\n\nLaut seinen Einwohnern ist Glyn Helyg das beste der drei Dörfer in den Starrentälern."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "62.62S"; 
          ["LONG"] = "25.90W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Lintrev"; 
      ["PROGRESS"] = "Findet Lintrev."; 
      ["LORE"] = "In Lintrev wohnen viele Bauern, die die Talbewohner mit Broten und Kuchen versorgen, dem Grundnahrungsmittel der Starren. Der Apfelgarten über den Ausläufern des Dorfs fällt den jungen, hungrigen Starrenkindern am häufigsten zum Opfer, aber die Bäume erholen sich schnell von diesen regelmäßigen Plünderungen.\n\nLaut seinen Einwohnern ist Lintrev das beste der drei Dörfer in den Starrentälern."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "61.89S"; 
          ["LONG"] = "24.33W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Echad Terthas"; 
      ["PROGRESS"] = "Findet Echad Terthas."; 
      ["LORE"] = "Von seinem Lager Echad Terthas wacht der Waldläufer Duindil über die Gefahren, die sich in West-Eregion und den nahegelegenen Ruinen von Caras Gelebren ausbreiten. Die alte Zitadelle des Elbenschmieds Celebrimbor wurde schon vor Tausenden von Jahren verlassen, aber in den letzten Nächten hat Duindil Feuer inmitten der zerstörten Mauern erspäht, und das Gebrüll der Uruk-hai die friedliche Abendbrise gestört."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "52.79S"; 
          ["LONG"] = "22.46W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Idhobel"; 
      ["PROGRESS"] = "Findet Idhobel."; 
      ["LORE"] = "Die Bewohner des Elbenlagers Idhobel behalten die Gefahren scharf im Auge, die in der letzten Zeit in West-Eregion aufgezogen sind. Die Uruk-hai, die Schwanenfleet heimsuchen, scheinen sich insbesondere für die Ruinen der Halle des Flinken Flusses zu interessieren, und die Elben von Idhobel stellen so oft wie möglich Überfalltrupps gegen sie auf die Beine."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "47.22S"; 
          ["LONG"] = "18.95W"; 
        }; 
      }; 
      };
  };
