  [1879450861] = {
      [1] = {
      ["NAME"] = "Heulsteine"; 
      ["PROGRESS"] = "Findet die Heulsteine."; 
      ["LORE"] = "Seit sich die Bewohner von Mooshang zurückerinnern können, sind die Heulsteine von Wölfen besiedelt, üblicherweise kommen sie der äußeren Palisade allerdings nicht zu nahe. Dies hat sich in den letzten Nächten geändert und die Stadtwachen werden nun beunruhigend oft im Dunkeln von wölfischen Augen angestarrt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "61.31S"; 
          ["LONG"] = "33.38W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Radlau"; 
      ["PROGRESS"] = "Findet Radlaw."; 
      ["LORE"] = "Niemand weiß mit Sicherheit, wann die Raufbolde nach Schwanenfleet kamen, aber die Starren haben den Eindruck, als wäre die Verbrechersiedlung Radlaw nahezu über Nacht errichtet worden. Nun ist davon abzuraten, sorglos durch die Starrentäler zu wandern, da die Raufbolde von Radlaw auf jede Gelegenheit gieren, den Unvorsichtigen Geld und Wertsachen abzunehmen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "59.73S"; 
          ["LONG"] = "29.29W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Fangsiedel"; 
      ["PROGRESS"] = "Findet Fangsiedel."; 
      ["LORE"] = "Das Watwasser steckt voller Gefahren, aber nur wenige Orte im Sumpf sind so bedrohlich wie Fangsiedel, wo die Avanc ihre Nester bauen. Lediglich die Dunländer aus dem nahegelegenen Lhan Garan wagen es, in der Umgebung dieses Nistplatzes auf Abenteuer zu gehen, aber selbst ihre besten Kundschafter ziehen es vor, sich dem Gebiet nur in allergrößter Not zu nähern."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "54.73S"; 
          ["LONG"] = "30.46W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Blutsenke"; 
      ["PROGRESS"] = "Findet die Blutsenke."; 
      ["LORE"] = "Die Wargbaue der Blutsenke sind voll von Knochenhaufen und den Kadavern längst getöteter Beute. Selbst die erfahrensten Krieger der Dunländer von Lhan Garan versuchen, diesen gefährlichen Ort tunlichst zu meiden - was allerdings nicht immer möglich ist: Bei der Ausbildung neuer Kundschafter muss geprüft werden, ob sie sich in gefährlichen Situationen gegen harte Gegner behaupten können. Und wer würde sich dafür besser eignen als die Warge, in der Blutsenke lauern?"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.83S"; 
          ["LONG"] = "21.63W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Schwanensenke"; 
      ["PROGRESS"] = "Findet die Schwanensenke."; 
      ["LORE"] = "Die Düsternetz-Spinnen der Schwanensenke dulden keinerlei Störung. Die Starren der nahegelegenen Dörfer Glyn Helyg und Clegur haben allerdings schon vor langer Zeit gelernt, sich nicht unter die vollgesponnenen Bäume zu wagen, und die Bewohner von Lintrev bleiben hauptsächlich auf ihren eigenen Höfen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "65.49S"; 
          ["LONG"] = "26.09W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Sharmokal"; 
      ["PROGRESS"] = "Findet Sharmokal."; 
      ["LORE"] = "Ein Überfalltrupp der Uruk-hai lagert unweit der Alten Südstraße, bei der Ruine eines alten Elbenturms. Welchen Zweck erfüllte der Turm wohl in vergangenen Zeitaltern? Die Uruk-hai wissen es nicht, und die Elben, die sich womöglich noch daran erinnern, ziehen es vor zu schweigen. Nun heißt dieser Ort Sharmokal und Reisende sollten ihm lieber nicht zu nahe kommen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "60.39S"; 
          ["LONG"] = "22.32W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Caras Gelebren"; 
      ["PROGRESS"] = "Findet Caras Gelebren."; 
      ["LORE"] = "Der große Elbenschmied Celebrimbor errichtete diese Stadt und schuf hier, am Zusammenfluss von Flüsse Sirannon und Glanduin, sein Vermächtnis. Caras Gelebren, die Silberne Bastion, war den Großteil des Zweiten Zeitalters hinweg ein Ort der Schönheit und des Lernens, aber nicht für die Ewigkeit bestimmt. Als das Böse in schöner Gestalt nach Caras Gelebren kam, ließ sich Celebrimbor täuschen: Gefangen in seinem eigenen Stolz und seiner Gier nach geheimem Wissen hätte der Elbenschmied beinahe die Welt zerbrochen.\n\nAls Sauron im Nachspiel von Celebrimbors Torheit Eregion mit Krieg überzog, wurde die Zitadelle zerstört und die Erinnerung an ihre friedlichen Tage ausgelöscht. In den folgenden Jahren sollte es Grund für Triumph geben, aber hinter seiner Fassade erinnern sich die Elben durchaus voller Kummer an das Fundament der Schande, auf dem er fußte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "53.62S"; 
          ["LONG"] = "20.92W"; 
        }; 
      }; 
      };
      [8] = {
