  -- Schwanenfleet & Cardolan / Gefahren von Cardolan
  [1879450925] = {
      [1] = {
      ["NAME"] = "Amon Firn"; 
      ["PROGRESS"] = "Erkundet den Amon Firn."; 
      ["LORE"] = "Das Gemetzel in der Schlacht vom Amon Firn war so drastisch, dass der Hügel fortan der 'Totenberg' genannt wurde. Zum Gedenken an die Gefallenen wurde ein großer Steinhügel errichtet.\n\nHier tötete Artandil, der letzte Dúnedain-König von Cardolan, König Malgun I. von Rhudaur und wurde selbst von ihm erschlagen. Seit jenem Tage blühen auf diesem Hügel rote Blumen."; 
      ["POINTS"] = {
