      };
      [2] = {
      ["NAME"] = "Caranost"; 
      ["PROGRESS"] = "Erkundet Caranost."; 
      ["LORE"] = "Caranost, einst eine große Stadt von Arnor und Herrschersitz von Prinzen, war auch als die Rote Festung bekannt. Sie wurde im Laufe von Cardolans Niedergang von Angmar geplündert.\n\nDie Türme von Caranost boten Ausblick auf einen großen Teil von Nord-Cardolan, bei der Eroberung der Stadt wurden allerdings viele von ihnen zerstört."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "45.17S"; 
          ["LONG"] = "40.65W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Dol Ernil"; 
      ["PROGRESS"] = "Erkundet Dol Ernil."; 
      ["LORE"] = "Die letzten Prinzen von Cardolan lebten in Dol Ernil neben den Hügelgräbern von Tyrn Gorthad. Nach der Verwüstung des Landes durch böse Zauberei und tödliches Eisen spuken in den Ruinen der Festung nun die wiederauferstandenen Toten."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "39.85S"; 
          ["LONG"] = "50.56W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Gaevarad"; 
      ["PROGRESS"] = "Erkundet Gaervarad."; 
      ["LORE"] = "Der Turm dieser bröckelnden Ruinen beherbergte einst ein mächtiges Artefakt: Ambossar, den schwarz geäderten Vandassar. Hier schmiedeten die Dúnedain einen Bund des Friedens mit den Amonedain von Cardolan. Die Anführer schworen Eide und das Hügelvolk wurde gefügig gemacht ... zumindest vorerst.\n\nDie Diener Angmars sollten den Turm später zerstören und den Vandassar zerschmettern, was das Hügelvolk aus seinem Bündnis befreite und zu dessen Gegenschlag führte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "53.61S"; 
          ["LONG"] = "53.53W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Gond Orchal"; 
      ["PROGRESS"] = "Erkundet Gond Orchal."; 
      ["LORE"] = "Die Zeit hat jegliche Erinnerung an die Erbauer des stehenden Steins von Gond Orchal getilgt und niemand weiß noch, für wen er errichtet wurde. Bei den Vorfahren der Amonedain galt dieser Ort als heilig, im Laufe der Jahre war er aber auch Schauplatz des Schreckens."; 
      ["POINTS"] = {
