      [8] = {
      ["NAME"] = "Netzlichtung"; 
      ["PROGRESS"] = "Erkundet die Netzlichtung."; 
      ["LORE"] = "Legenden des Landes zufolge war der Hügel von Netzlichtung einst von üppigen Terrassengärten überzogen. Lange, bevor er zur Heimstatt der schrecklichen Spinnen mit ihrer tödlichen Seide verkam, war er voller wunderschöner Blumen, Früchte und Brunnen.\n\nEs heißt, dass in uralter Zeit sogar Herrin Galadriel persönlich seine Pfade besuchte und lächelnd zwischen seinen Rosenblüten wandelte."; 
      ["POINTS"] = {
        [1] = {
