      };
      [11] = {
      ["NAME"] = "Hove-Bauernhof"; 
      ["PROGRESS"] = "Erkundet den Hove-Bauernhof."; 
      ["LORE"] = "Die Familie Hove mag nicht ganz so alteingesessen wie ihre Nachbarn vom Scurloc-Hof sein, verdient sich aber auf ihrem kleinen Hof einen hartnäckigen, wenn auch beschwerlichen Lebensunterhalt. In den letzten Tagen wurde die Familie allerdings von Bilwiss-Plünderern aus dem Norden vertrieben."; 
      ["POINTS"] = {
