  [1879450926] = {
      [1] = {
      ["NAME"] = "Eid-Tafel"; 
      ["PROGRESS"] = "Entdeckt die Eid-Tafel."; 
      ["LORE"] = "Die Inschrift auf dieser Tafel ist alt und verwittert, aber Ihr könnt immer noch ein paar Worte erkennen. Sie scheinen auf die Eide anzuspielen, die hier vor Hunderten von Jahren geschworen wurden, als das Hügelvolk gezwungen war, sich den Königen der Dúnedain zu unterwerfen."; 
