      };
      [2] = {
      ["NAME"] = "Ausrüstung der Gefallenen"; 
      ["PROGRESS"] = "Findet die Ausrüstung der Gefallenen."; 
      ["LORE"] = "Obwohl sie beim zweiten Untergang Angmars ums Leben kam, haben die Anhänger der Eisernen Krone die Angmarim-Zauberin Múra von den Trév Duvárdain nicht vergessen. Nach der Rückkehr der Eisernen Krone nach Carn Dûm wurden ihre einstigen Waffen, ein Stab und eine Hellebarde, geborgen und feierlich an den Wänden Gwathrenosts aufgehängt. Wer nicht den Zorn der Eisernen Krone auf sich ziehen will, sollte sie tunlichst nicht anfassen."; 
      ["POINTS"] = {
