  -- Rückkehr nach Carn Dûm / Sagroth, Hort des Ungeziefers: Die Sucher von Sagroth
  [1879460770] = {
      [1] = {
      ["NAME"] = "Schwammige Ausdünstungen"; 
      ["PROGRESS"] = "Entdeckt schwammige Ausdünstungen."; 
      ["LORE"] = "Cruim setzte sich auf die trockensten Steine, die sie finden konnte und zog ihren linken Stiefel aus. Etwas Feuchtes, Schwammiges, das aussah wie ein köstlicher Pudding und nach altem Fleisch roch, flutschte heraus, als sie ihn umdrehte. Dies war der Preis, den sie dafür zahlen musste, sich zu weit in die Nähe einer schneckenartigen Kreatur gewagt zu haben.\n\n'Ich hasse diesen Ort', erklärte sie.\n\n'Wäret Ihr lieber im Glashaus?', fragte der spindeldürre Brâc, der mehr Verstand als Muskeln besaß. Er gab zwar einen armseligen Soldaten ab, aber die Eiserne Krone wurde mit jedem Tag verzweifelter. Deshalb hatten sie sich überhaupt erst in die Kanalisation begeben."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.62N"; 
          ["LONG"] = "48.98E"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Fußspuren"; 
      ["PROGRESS"] = "Findet Fußspuren."; 
      ["LORE"] = "Eine Weile lang hörten die beiden nur den Widerhall ihrer Schritte im Tunnel.\n\n'Was glaubt Ihr, will Gárvadach hier finden?', fragte Cruim in die Stille hinein.\n\nIhr Gefährte zuckte mit den Schultern. 'Keine Ahnung', gestand er. 'Der Gestank vernebelt mir die Sinne.'n\n'Vielleicht hat er ja etwas mit dem Gestank vor.' In einem Versuch, Gárvadachs aufbrausendes Getue nachzuäffen, verschränkte sie die Arme und rümpfte die Nase. 'Kniet vor mir nieder, sonst sorge ich dafür, dass Ihr stinkt wie tausend vergammelte Zwiebeln!'\n\nBrâc verdrehte die Augen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.64N"; 
          ["LONG"] = "48.62E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Klebrige Pfütze"; 
      ["PROGRESS"] = "Findet eine besonders klebrige Pfütze."; 
      ["LORE"] = "Als Brâc sein Schwert aus dem Schneckenkadaver zog, klebten lange Schleimfäden daran. Das Schwert zu schwenken, half nicht, es von ihnen zu befreien. Er verzog das Gesicht.\n\nCruim ging in einem großen Bogen an ihm und der sich rasch auflösenden Schnecke vorbei. 'Ihr könntet die Klinge in Wasser tauchen', schlug sie vor. 'Das würde bestimmt helfen.'\n\nEr betrachtete das kränklich grüne Wasser misstrauisch. 'Eher würde ich das Schwert komplett austauschen.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "77.39N"; 
          ["LONG"] = "48.71E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Leiche eines Soldaten"; 
      ["PROGRESS"] = "Findet eine bestimmte Leiche."; 
      ["LORE"] = "In den Tiefen der Kanalisation stolperten sie über die Überreste Ogils. Der Großteil der Leiche war bereits von der dort beheimateten widerlichen Fauna verzehrt worden, sodass er kaum noch zu erkennen war.\n\nBrâc betrachtete die Überreste seines einstigen Freundes. 'Wir werden hier bei der Suche nach dem Schatz eines Wahnsinnigen zugrundegehen.'\n\nCruim packte ihn am Handgelenk und zog ihn dorthin zurück, wo sie hergekommen waren. 'Nein', sagte sie schlotternd. 'Die komplette Stadt ist dem Wahnsinn verfallen. Ich werde nicht darin sterben!'\n\nEr befreit seine Hand aus ihrem Griff. 'Was habt Ihr dann vor?'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "77.59N"; 
          ["LONG"] = "48.24E"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Schleimiges Schwert"; 
      ["PROGRESS"] = "Findet ein schleimiges Schwert."; 
      ["LORE"] = "'Wir gehen los und halten erst wieder an, wenn wir Angmar verlassen haben', flüsterte Cruim.\n\nSie gingen zum Tor Carn Dûms. Eine unsichtbare Wand aus Gestank verbarg sie vor Vorbeigehenden.n\n'Irgendjemand wird uns aufhalten', sagte Brâc. 'Sie werden erkennen, dass wir desertieren.'\n\nDaraufhin höhnte sie: 'Wir stinken, als hätte jemand ein Haarbüschel in einen Abort geschmissen und es dann angezündet. Keiner wird sich in unsere Nähe wagen.'\n\nSie schwiegen, bis sie die Tore Carn Dûms weit hinter sich gelassen hatten.\n\n'Wohin gehen wir?', fragte Brâc.\n\n'Das ist egal, Hauptsache, wir sind nicht mehr an diesem verfluchten Ort.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.42N"; 
          ["LONG"] = "49.13E"; 
        }; 
      }; 
      };
  };
