  -- Östliches Königreich Gondor / Die Rückeroberung Lossarnachs
  [1879464437] = {
      [1] = {
      ["NAME"] = "Arnach"; 
      ["PROGRESS"] = "Findet Arnach."; 
      ["LORE"] = "Fürst Forlong, der Herrscher von Lossarnach, unterhielt eine große Halle im Dorf Arnach, bis er während des Ringkriegs nach Minas Tirith ritt. Er fiel im Kampf auf dem Pelennor und seine Halle steht nun leer. Die Bewohner von Arnach trauern um ihren allseits beliebten Fürsten und geben ihr Bestes, um ohne ihn nach vorne zu sehen. Arnach bleibt eine geschäftige, lebendige Siedlung für Kaufleute und Handwerker, leidet jedoch noch immer an der schmerzhaften Erinnerung an die Angriffe der Haradrim in den vergangenen Monaten."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.55S"; 
          ["LONG"] = "24.40W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Imloth Melui"; 
      ["PROGRESS"] = "Findet Imloth Melui."; 
      ["LORE"] = "Imloth Melui, das Tal der lieblichen Blumen, ist die Heimat von Herrin Vanyalos, der Schwester des Truchsesses Denethor und Gemahlin des Fürsten Forlong von Lossarnach. Beide diese Männer fielen im Krieg, einer innerhalb der Mauern der Weißen Stadt, der andere außerhalb. Die Wunden ihres Verlusts schmerzen Vanyalos besonders, denn sie hatte zuvor vom Tod der beiden geträumt und muss mit der Last leben, ihn nicht verhindert zu haben.\n\nViele der Heiler in den Häusern der Heilung in Minas Tirith stammen aus Imloth Melui - auch Ioreth, die ein wenig über beeindruckend viele Dinge weiß, und beeindruckend viel über sehr wenig."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "73.20S"; 
          ["LONG"] = "24.79W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Nan Harlond"; 
      ["PROGRESS"] = "Findet den Nan Harlond."; 
      ["LORE"] = "Die Felder und Höfe des Nan Harlond säumen die Straße unter dem Südhafen Gondors. Sie werden nicht länger von Saurons Armeen bedroht, gelegentlich aber doch von einzelnen Feinden, die von ihren geschlagenen Verbündeten zurückgelassen wurden. Die natürliche Schönheit von Lossarnach kämpft darum, sich nach dem Ringkrieg wieder zu entfalten, und die Blumen hier sind ein Beleg für diese Hartnäckigkeit."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "74.29S"; 
          ["LONG"] = "21.04W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Rammas Echor"; 
      ["PROGRESS"] = "Findet die Rammas Echor."; 
      ["LORE"] = "Die große Mauer namens Rammas Echor, die Minas Tirith und die Felder des Pelennor umschließt. Während des Kriegs wurde die Mauer an verschiedenen Stellen durchbrochen und die Truppen Saurons strömten durch die Breschen, um die Weiße Stadt zu belagern. Nun schuften Zwerge und Arbeiter aus Gondor, um die Schäden am Verteidigungswall zu reparieren - obwohl alle hoffen, dass die Rammas viele Jahre lang nicht mehr gebraucht werden wird."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "72.14S"; 
          ["LONG"] = "18.75W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Der Harlond"; 
      ["PROGRESS"] = "Findet den Harlond."; 
      ["LORE"] = "In den Tagen vor der Schlacht auf dem Pelennor wurde der Harlond von Orks aus Mordor gehalten, die so viele der gondorischen Schiffe verbrannten wie nur möglich. Eines Tages kamen Korsarenschiffe mit schwarzen Segeln von Süden her den Fluss hinaufgesegelt. Plötzlich aber hissten die Schiffe das Banner des Königs, denn sie wurden von Aragorn und den Verbündeten Gondors gesteuert, die die Orks überrumpelten. Ihr Eintritt in die Schlacht erwies sich als entscheidend für die Verteidigung von Minas Tirith und den Sieg über Saurons Kräfte.\n\nHeute arbeiten die Männer Gondors und die Zwerge aus dem Weißen Gebirge gemeinsam daran, den Südhafen wiederaufzubauen. Schon bald soll der Fluss wieder Waren und Güter zu Häfen in nah und fern tragen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "72.72S"; 
          ["LONG"] = "20.01W"; 
        }; 
      }; 
      };
  };
  -- Östliches Königreich Gondor / Die Rückeroberung Ober-Lebennins
  [1879464470] = {
      [1] = {
      ["NAME"] = "Minlos"; 
      ["PROGRESS"] = "Findet Minlos."; 
      ["LORE"] = "Das Leuchtfeuer von Minlos liegt Minas Tirith am nächsten und wurde während des Kriegs von den Haradrim erobert, die einen Angriff auf das Dorf Arnach unternehmen wollten. Nun wurde das Leuchtfeuer gelöscht und die Bauern der Gegend hoffen, dass es viele Jahre lang nicht mehr entzündet wird - als Zeichen, dass die Gefahr gebannt ist."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.83S"; 
          ["LONG"] = "25.96W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Halach"; 
      ["PROGRESS"] = "Findet Halach."; 
      ["LORE"] = "Der kleine Hafen Halach wurde im Krieg den Haradrim überlassen, doch nach dem Sieg über Sauron konnten die Dorfbewohner zurückkehren und mit dem langen Prozess beginnen, ihr Leben und ihre Häuser wiederaufzubauen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "82.26S"; 
          ["LONG"] = "26.21W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Glaniath"; 
      ["PROGRESS"] = "Findet Glaniath."; 
      ["LORE"] = "Das kleine Bauerndorf Glaniath wurde im Krieg von Haradrim geplündert. Nun, nach Kriegsende, versuchen seine Bewohner, ihr Leben weiterzuleben. Manche hier glauben, dass Sirgon nach seiner Gefangenschaft in den Händen der Haradrim nicht mehr in der Lage ist zu herrschen, und dass sein Sohn Faltharan stattdessen Fürst von Lebennin werden sollte."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "80.62S"; 
          ["LONG"] = "29.70W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tumladen"; 
      ["PROGRESS"] = "Findet Tumladen."; 
      ["LORE"] = "Tumladen ist die große Zuflucht Gondors, ähnlich wie Helms Klamm im Königreich Rohan. Im Krieg suchten viele Schutz in Tumladen, darunter auch eine der reizbaren Schwestern von Truchsess Denethor, die meinungsstarke Herrin Terenis. Sehr zu Hauptmann Imladirs Erleichterung, der für Tumladens Verteidigung zuständig ist, ist Herrin Terenis nun nach Minas Tirith zurückgekehrt. Die Zuflucht ist nicht mehr überfüllt von Vertriebenen aus Gondor."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.84S"; 
          ["LONG"] = "29.35W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Pelargir"; 
      ["PROGRESS"] = "Findet Pelargir."; 
      ["LORE"] = "Die Hafenstadt Pelargir war im Ringkrieg Schauplatz der entscheidenden Schlacht gegen die Korsaren aus Umbar. Aragorn führte die Armee der Toten an, um die Schiffe der Korsaren zu erobern. Balakhôr die Geißel fand auf seinem großen Schiff den Tod, der Nachtjuwel, und man hofft, dass Castamirs Erben durch seinen keine Gefahr mehr darstellen werden.\n\nDie gondorische Flotte ist nach dem Ringkrieg arg dezimiert. Sirgon, der Fürst von Lebennin, muss die Schiffe und die Seemacht des Königreichs eiligst wiederaufbauen, weshalb durch Pelargir Tag und Nacht der Klang von Hammern und Äxten dröhnt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "84.01S"; 
          ["LONG"] = "33.18W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Sirthanc"; 
      ["PROGRESS"] = "Findet Sirthanc."; 
      ["LORE"] = "Das Leuchtfeuer von Sirthanc wurde im Krieg entzündet, um Krieger zur Verteidigung von Minas Tirith zu rufen. Nun aber wurde es gelöscht, und wenn Gondors Glück anhält, bleibt es das auch über viele Jahre hinweg. In Sirthanc wurde Fürst Sirgon aus der Gefangenschaft der Haradrim befreit, und nun glaubt ein Teil seines Volks, dass man ihm gestatten sollte, als Fürst von Lebennin abzutreten, damit seine körperlichen und geistigen Wunden verheilen können."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "79.35S"; 
          ["LONG"] = "31.96W"; 
        }; 
      }; 
      };
  };
