      [2] = {
      ["NAME"] = "Lhingar"; 
      ["PROGRESS"] = "Findet Lhingar."; 
      ["LORE"] = "Die Spinnen von Lhingar wurden durch den tosenden Ringkrieg nicht behindert - und auch durch dessen Ende nicht. Sie krabbeln noch immer zwischen den Bäumen umher, spinnen Netze, um ihre Beute zu fangen, und ernähren sich von jedem, der ihrem Versteck zu nahekommt."; 
