  -- Östliches Königreich Gondor / Die Rückeroberung Nieder-Lebennins
  [1879464488] = {
      [1] = {
      ["NAME"] = "Erynos"; 
      ["PROGRESS"] = "Findet Erynos."; 
      ["LORE"] = "Die Siedlung Erynos war Schauplatz eines der merkwürdigsten Vorkommnisse es Ringkriegs: Vor den Mauern der Stadt wurden die Leichname einer Gruppe von Korsaren gefunden, die offenbar ertrunken waren. Manche glauben, dies sei das Werk der Fünf Schwestern von Lebennin gewesen, der Flussmaiden, die der Legende nach die Lande beschützen, durch die ihre Flüsse fließen. Andere dagegen verweisen diese Gestalten ins Reich der Fabeln.\n\nHeute arbeiten die Bewohner von Erynos hart daran, ihr Leben wiederaufzubauen und den Nachbarn zu helfen, die im Krieg besonders gelitten haben."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "80.08S"; 
          ["LONG"] = "32.80W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Feste Anglebed"; 
      ["PROGRESS"] = "Findet die Feste Anglebed."; 
      ["LORE"] = "Die große Feste Anglebed ist Heimat der Hüter des Leuchtfeuers von Amon Anglebed, das im Ringkrieg entzündet wurde. Im Gegensatz zu den anderen Leuchtfeuern Gondors bleibt das Leuchtfeuer hier trotz des Friedens des Königs entzündet. Die Soldaten wachen nun über die Dorfbewohner, aus Angst, dass einige Verbündete des untreuen Malthellim womöglich dessen Werk fortsetzen und die Stadt den Feinden Gondors überlassen wollen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "78.98S"; 
          ["LONG"] = "39.14W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Malbarth"; 
      ["PROGRESS"] = "Findet Malbarth."; 
      ["LORE"] = "Das kleine Dorf Malbarth wurde im Krieg weitestgehend von Kampfhandlungen verschont, doch Nachrichten aus anderen Gegenden Gondors machten den Dorfbewohnern große Angst, dass ihre Häuser nicht mehr lange sicher bleiben würden. Selbst jetzt noch gibt es Gemunkel, dass einige Korsaren aus der Schlacht bei Pelargir geflohen sein sollen und sich nun hier in der Wildnis verstecken und auf eine Gelegenheit warten, das Dorf einzunehmen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "80.14S"; 
          ["LONG"] = "42.38W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Linhir"; 
      ["PROGRESS"] = "Findet Linhir."; 
      ["LORE"] = "Die Stadt Linhir liegt am Fluss Gilrain. Während des Ringkriegs verdüsterte Saurons schändliche Magie den Himmel über Linhir. Die Söhne von Comeldir organisierten die Verteidigung, und als die Kämpfe endlich endeten, standen die Bewohner noch: stark, fest und vereint. Heute, da die Gefahr des Krieges hinter ihnen liegt und der Himmel wieder hell und klar ist, gehen die Bürger von Linhir wieder ihrem Alltag nach."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "81.41S"; 
          ["LONG"] = "45.43W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Sarnhad"; 
      ["PROGRESS"] = "Findet Sarnhad."; 
      ["LORE"] = "Dieses kleine Dorf am Ufer des Serni blieb im Krieg von den meisten Kampfhandlungen verschont, doch die Berichte von Korsaren und Halbtrollen, die benachbarte Städte angriffen, erfüllten die Bewohner mit großer Sorge - genau wie die Verdunkelung des Himmels durch den Tag ohne Morgen. Heute wollen die Bewohner von Sarnhad nichts weiter, als zu ihrem Alltag zurückzukehren."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "81.59S"; 
          ["LONG"] = "43.90W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Zarsatrâd"; 
      ["PROGRESS"] = "Findet Zarsatrâd."; 
      ["LORE"] = "Zarsatrâd liegt eingebettet in den Wald südlich der Zwergenfestung Furukzahar und dient als Außenposten für die Temámir-Zwerge, die nun im Weißen Gebirge leben. Aus ihrem Steinbruch karren sie Steine an verschiedenste Orte in Gondor, um ihren Nachbarn freundlicherweise beim Wiederaufbau nach dem Ringkrieg zu helfen ... gegen einen angemessenen Preis."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "77.80S"; 
          ["LONG"] = "34.56W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Furukzahar"; 
      ["PROGRESS"] = "Findet Furukzahar."; 
      ["LORE"] = "Die Festung Furukzahar ist eine Bastion der Sicherheit für Temámir-Zwerge aus dem Weißen Gebirge. Unter der Herrschaft ihres Anführers Hámisar sind Zwerge aller Sippen gern gesehene Gäste. Das Verhältnis zu den Menschen von Gondor ist im Augenblick jedoch etwas unterkühlt. Hámisars Entscheidung, die Tore von Furukzahar zu verschließen und sich nicht am Ringkrieg zu beteiligen, hat den Zwergen bei den Bewohnern von Lebennin keine Sympathien eingebracht."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "70.34S"; 
          ["LONG"] = "35.35W"; 
        }; 
      }; 
      };
  };
