      [4] = {
      ["NAME"] = "Ruinen von Ethillorn"; 
      ["PROGRESS"] = "Findet die Ruinen von Ethillorn"; 
      ["LORE"] = "Als die Korsaren aus Umbar in Saurons Namen Gondor zu erobern versuchten, brannten sie auch den Hafen von Ethillorn nieder. Nun, da der Krieg vorüber ist, sind die Korsaren verschwunden, doch ein noch viel dunkleres Übel ist nach Ethillorn gekommen und bedroht die umliegenden Lande."; 
      ["POINTS"] = {
