  -- Westliches Königreich Gondor / Erkunder / Erkunderin des Schwarzgrundtals
  [1879466366] = {
      [1] = {
      ["NAME"] = "Fennas Dûr"; 
      ["PROGRESS"] = "Findet Fennas Dûr."; 
      ["LORE"] = "Von der Schwelle des Dunklen Tors im Schatten des Dimholt von Rohan ausgehend schneiden die Pfade der Toten einen großen Tunnel durch den Dwimorberg, auch Spukberg genannt. Die Pfade führen nach Fennas Dûr im Schwarzgrundtal in Gondor. Durch Fennas Dûr folgten viele der Toten Menschen von Dunharg König Elessar zum Stein von Erech und zur Erfüllung ihres Eids beim Pelargir. Die wenigen Reulosen, die dem Ruf nicht folgten, brachen ihren Eid jedoch gleich doppelt, vom eigenen Willen erneut zu ihrem Schicksal verdammt. Nun verweilen diese Zügellosen Schatten noch immer hier, in den Schatten des Schwarzgrundtals, und suchen die Gegend heim. Sie wissen nicht, ob der Fluch, mit dem Isildur sie belegt hat, je aufgehoben wird ... und vielleicht schert es sie auch nicht mehr."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "53.15S"; 
          ["LONG"] = "66.85W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Morlad"; 
      ["PROGRESS"] = "Findet Morlad."; 
      ["LORE"] = "Am Südhang des Weißen Gebirges, am Fluss Morthond, liegt das Zentrum des Schwarzgrundtals, die ummauerte Stadt Morlad. Auf ihren verschlungenen Straßen steht das Anwesen des Fürsten des Schwarzgrundtals, Duinhir, und seiner Gemahlin Rosfin. Als Gondor in den Krieg gerufen wurde, führten Fürst Duinhir und seine beiden Söhne Duilin und Derufin gute fünfhundert Bogenschützen nach Minas Tirith, um die Stadt gegen die Armeen von Mordor zu verteidigen. Nach Kriegsende kehrte Fürst Duinhir mit seinen Männern nach Morlad zurück. Schnell verbreitete sich die Kunde vom Tod seiner Söhne und auch die Nachricht, dass man versucht hatte, dem Fürsten den Mord an einem Gesandten der Haradrim anzuhängen. Trotz Gondors Sieg ist Morlad zu einem tristen, düsteren Ort geworden, und viele der Bewohner sind überzeugt, dass König Elessar sie nach dem Ringkrieg einfach fallengelassen hat."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.84S"; 
          ["LONG"] = "67.55W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Alagrant"; 
      ["PROGRESS"] = "Findet Alagrant."; 
      ["LORE"] = "Der Weiler Alagrant liegt eingebettet in die Wälder westlich des Hügels von Erech und wird größtenteils von Jägern und Händlern aus dem Schwarzgrundtal bewohnt."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "58.61S"; 
          ["LONG"] = "68.59W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tingobel"; 
      ["PROGRESS"] = "Findet Tingobel."; 
      ["LORE"] = "Das Bergbaudorf Tingobel wurde zwar während des Ringkriegs von den Orks aus dem Weißen Gebirge geplündert, inzwischen jedoch auf Befehl von Fürst Duinhir zurückerobert. Die beschädigten Häuser wurden repariert, die Mine wiederhergestellt. Wer aber im Schwarzgrundtal lebt, der hat nicht vergessen, wie viele beim Fall des Dorfs ihr Leben gelassen haben."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.13S"; 
          ["LONG"] = "64.04W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Sardol"; 
      ["PROGRESS"] = "Findet Sardol."; 
      ["LORE"] = "Südlich von Alagrant, am Fluss Morthond, liegt Sardol, ein von Bauern und Fischern bewohnter Weiler. Bevor die Erben von Castamir die inzwischen zerstörte Blockade auf der Straße durch Tarlangs Krone errichteten, diente Sardol als kleiner Handelsposten für Reisende zwischen Morlad und Dol Amroth."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "60.97S"; 
          ["LONG"] = "69.74W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Lancrath"; 
      ["PROGRESS"] = "Findet Lancrath."; 
      ["LORE"] = "Östlich des Hügels von Erech, unmittelbar neben dem Tarlangs Hals genannten Pass, liegt Lancrath, ein Weiler von Bauern, Handwerkern und Händlern. Vor Fürst Duinhirs Rückkehr aus dem Krieg und der Rückeroberung von Tingobel suchten viele der Flüchtlinge aus dem Bergbaudorf Zuflucht und fanden sie bei den freundlichen Bewohnern von Lancrath."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "58.19S"; 
          ["LONG"] = "62.98W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Eryn Asgath"; 
      ["PROGRESS"] = "Findet den Eryn Asgath."; 
      ["LORE"] = "Im Waldland südöstlich des Hügels von Erech bei Tarlangs Hals liegt der Eryn Asgath, das Versteck der Wölfe, die aus Lamedon und den umliegenden Hügeln hier eingedrungen sind. Schauerliches Heulen hallt durch die Nacht, denn die Wölfe haben nach Ende des Ringkriegs neue Sorgen ins Schwarzgrundtal gebracht."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "60.29S"; 
          ["LONG"] = "64.68W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Lhingroth"; 
      ["PROGRESS"] = "Findet Lhingroth."; 
      ["LORE"] = "In den Hochlanden östlich des Weilers Sardol graben sich die Höhlen von Lhingroth unter Tarlangs Gipfel. Die Höhlen werden zwar seit langem von den Spinnen des Schwarzgrundtals bewohnt, waren in früheren Zeiten aber wohl auch den Bergmenschen bekannt, denn in der Dunkelheit von Lhingroth wurden Tote gesichtet."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "47.88S"; 
          ["LONG"] = "7.11W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Hügel von Erech"; 
      ["PROGRESS"] = "Findet den Hügel von Erech."; 
      ["LORE"] = "Der Hügel von Erech erhebt sich am Eingang zum Schwarzgrundtal. Er ist ein sehr geschichtsträchtiger Ort in Gondor. Im Zweiten Zeitalter hat Isildur hier den Stein von Erech platziert. Hier haben die Bergmenschen ihren Eid geleistet, Gondor zu helfen. Und hier haben sie Isildur verraten und wurden verflucht. Der Hügel von Erech hat bei den Bewohnern des Schwarzgrundtals einen schlechten Ruf. Sie fürchten ihn sogar, denn sie glauben, dass hier die Toten spuken."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "58.26S"; 
          ["LONG"] = "64.86W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Stein von Erech"; 
      ["PROGRESS"] = "Findet den Stein von Erech"; 
      ["LORE"] = "Der Stein von Erech wurde fern der Zerstörung von Númenor geboren. Er thront auf dem Hügel von Erech im Schwarzgrundtal. In Wahrheit ist der Stein von Erech einer der sieben Vandassar, der Eidsteine, und heißt Orossar. Das wissen jedoch nur noch wenige der größten Gelehrten von Mittelerde, alle anderen haben es vergessen. Auf den Stein von Erech haben die Bergmenschen vor langer Zeit Isildur einen Eid geschworen, dass sie Gondor im Krieg gegen den Dunklen Herrscher Sauron unterstützen würden. Als sie sich jedoch weigerten, diesen Eid zu ehren, verfluchte Isildur sie. Sie verkümmerten und verkamen zu den Schlaflosen Toten. Viele der Toten sind inzwischen zwar verschwunden, da sie ihren Eid eingelöst haben, doch der Stein von Erech ruht noch immer auf dem Hügel, eine ewige Erinnerung an den Verrat der Bergmenschen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "58.05S"; 
          ["LONG"] = "65.05W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Azruthors Reue"; 
      ["PROGRESS"] = "Findet Azruthors Reue."; 
      ["LORE"] = "Auf Befehl von Balakhôr der Geißel, einem der Erben von Castamir, errichtete Azruthor bei Tarlangs Krone eine Blockade. Dort sollten sich die Pläne der Geißel entfalten, Anfalas zu brandschatzen und seine Küsten für Umbar zu erobern. Azruthor gelang es jedoch nicht, die Lande von Gondor zu erobern. Nachdem seine Kompanie von Korsaren bezwungen war und sein vermeintliches Bündnis mit den Zügellosen Schatten gebrochen, fand er durch die Hände mutiger Abenteurer sein Ende und die Blockade wurde zerstört. Nach dem Ringkrieg ist von Azruthors großem Plan nur noch ein halb zerstörtes Lager inmitten einer zersplitterten Blockade bei Tarlangs Krone übrig."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "67.28S"; 
          ["LONG"] = "68.88W"; 
        }; 
      }; 
      };
  };
