  -- Die Schildinseln / Bewohner der Schildinseln
  [1879469676] = {
      [1] = {
      ["NAME"] = "Das Ende der Reise"; 
      ["PROGRESS"] = "Sucht das Ende der Reise."; 
      ["LORE"] = "Die Gewässer um Zîrar Tarka sind tückisch für jeden, der sie unvorbereitet befährt, und selbst erfahrene Seefahrer können zu ihrem Leid den Inseln zu nahe kommen, wenn es in der Bucht von Belfalas stürmt. Sind die Valar ihnen hold, überlebt ein Teil der Mannschaft und kann den Sonnenaufgang durch das Blätterdach des Waldes beim Ende der Reise erleben. Haben die Seefahrer Pech, bleiben nur zertrümmerte, in der Umgebung der Inselkette aus dem Wasser ragende Rümpfe übrig, die von da an das Lächeln von Shedax Khîn, den Mahlenden Mäulern, zieren."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.72S"; 
          ["LONG"] = "130.19W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Halrax"; 
      ["PROGRESS"] = "Sucht Halrax."; 
      ["LORE"] = "Die große Siedlung Halrax steht auf Zamarzîr, der größten Insel der Zîrar Tarka. Sie wurde über viele Jahre hinweg von den Freibeutern der Bucht von Belfalas errichtet, und obwohl die meisten Bewohner nur ein paar Jahre dort bleiben, haben sie alle eines gemein: Freiheit, Eigenständigkeit und Unabhängigkeit sind ihnen wichtiger als alles andere. Halrax ist eine Heimat der Verlorenen, in der sich jene, die sonst kein Zuhause haben, wie durch Zufall wiederfinden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.00S"; 
          ["LONG"] = "126.30W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Kafagur"; 
      ["PROGRESS"] = "Sucht Kafagur."; 
      ["LORE"] = "Die Bewohner der anderen Teile der Zîrar Tarka halten die Insel Jeb Khâtel für unbewohnt, und die Orkstimmen, die dort von den hohen Klippen herabschallen, ermutigen nicht gerade zu einem Besuch. Trotzdem ist Kafagur zur neuen Heimat für unerwartete Reisende fernab ihrer angestammten Lande geworden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.55N"; 
          ["LONG"] = "135.10W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Phadâl Bimai"; 
      ["PROGRESS"] = "Findet Phadâl Bimai."; 
      ["LORE"] = "'Spitzt die Ohren, mein Freund / meine Liebe', sagt Manadhon vorsichtig, während er einen Krug mit Bier füllt. Der Wirt von Halrax fixiert Euch zwar, ist aber im Einschenken so geübt, dass er keinen Tropfen verschüttet.\n\n'Niemand geht so weit nach Norden. Und wisst Ihr auch, warum?'\n\nIhr schüttelt den Kopf.\n\n'Auf Umbari heißt Phadâl Bimai ... 'Wrack der Unmenschen'. Hier seid Ihr besser dran, glaubt mir. Es soll dort gefährlich sein.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.34N"; 
          ["LONG"] = "136.72W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Aklâradum"; 
      ["PROGRESS"] = "Sucht Aklâradum."; 
      ["LORE"] = "'Woher kennt Ihr diesen Namen?', fragt Manadhon. Ihr erwidert, dass Ihr ihn im Vorbeigehen aufgeschnappt habt. Der Wirt von Halrax überlegt kurz.\n\n'Ich war zwar selbst noch nie dort oben, aber mir ist es immer durch Mark und Bein gegangen, wenn die, die dort haust, über die Stadt geflogen ist und der Schatten ihrer Flügel auf mir lag. Ich glaube, das ist ein böses Omen, mein Freund / meine Liebe, und Ihr solltet den Namen Aklâradum lieber schnell wieder vergessen. Sie wohnt dort, und solange sie zu Hause bleibt, können wir in Frieden leben. Zumindest ... habe ich das immer gedacht.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.10S"; 
          ["LONG"] = "125.48W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Irkhadâr"; 
      ["PROGRESS"] = "Sucht Irkhadâr."; 
      ["LORE"] = "'Auf der Insel Zamarzîr fällt einem recht schnell der Krater neben unserer Stadt auf', sinniert Manadhon beim Sonnenuntergang. 'Das war nicht immer ein See, wisst Ihr? Es soll mal ein Vulkan gewesen sein ... hoffen wir, dass er weiterhin schläft. Aber der Name ... manchmal frage ich mich, ob in dem Namen nicht ein Körnchen Wahrheit steckt.'\n\nIhr fragt Manadhon, welchen Namen er meint, und er schaut Euch direkt in die Augen.\n\n'Irkhadâr', antwortet er. 'Auf Umbari heißt das 'Der Schlafende'.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.65S"; 
          ["LONG"] = "126.00W"; 
        }; 
      }; 
      };
  };
