      };
      [10] = {
      ["NAME"] = "Ghar Jâdam"; 
      ["PROGRESS"] = "Sucht Ghar Jâdam."; 
      ["LORE"] = "Ghar Jâdam, die 'Knochengrotte', liegt auf Zamarzîr, der größten Insel der Inselkette Zîrar Tarkas. Dort leben riesige, sehr starke Dschungeltiere, und die klügeren Bewohner von Halrax haben gelernt, einen weiten Bogen um sie zu machen. Die weniger klugen unter ihnen dekorieren die Grotte allerdings weiterhin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.46S"; 
