  -- Misty Mountains / The High Passes
  [1879071651] = {
      [1] = {
      ["NAME"] = "Cirith Imladris"; 
      ["LORE"] = "Cirith Imladris acts as the lone gateway between the Valley of Rivendell and the high passes of the Misty Mountains. Once there was fair trade along this route between the dwarf-realms of the mountains and the Elves of Rivendell, but the dwarves were driven out long ago by goblins, and many other hazards have returned to make travel up through this region difficult indeed. For the most part, the Elves keep watch over Cirith Imladris, and few pass beyond its upper reaches into the Misty Mountains."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.41S"; 
          ["LONG"] = "3.48W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Whitcleft"; 
      ["LORE"] = "The difficult yet unassuming pass of Whitcleft turned out to be the downfall of one of the larger dwarf-realms within the northern reaches of the Misty Mountains. Believing that the goblins to the north would resolutely struggle to find or dig secret tunnels into their well-defended mines to attack, the dwarves to the south neglected the defence of this pass.\n\nA particularly cunning goblin chieftain led a horde of his people through the pass of Whitcleft in the deep of winter, forcing them to dig their way through at a terrible cost in lives -- but using this approach he came upon the dwarves from the surface unexpectedly and trapped them underground through the long months that followed, driving most to their death through starvation or futile attempts to escape the siege.\n\nSo many goblins froze during the siege that they too were decimated by it, but Durin's Folk were never able to return to their lost stronghold."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.23S"; 
          ["LONG"] = "0.16E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Rakhâs-bizar"; 
      ["LORE"] = "Rakhâs-bizar has never been well-travelled by the Free Peoples of Middle-earth. For time immemorial, giants have lived in the peaks and valleys to the south, and only those rare few who might call them friends, or who wish to test their mettle against a truly powerful and capricious foe might have reason to travel there. Not many of the latter return."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "27.39S"; 
          ["LONG"] = "2.60E"; 
        }; 
      }; 
      };
  };
  -- Misty Mountains / Ruins of the Misty Mountains
  [1879071652] = {
      [1] = {
      ["NAME"] = "Glóin's Camp"; 
      ["LORE"] = "Only here can the Longbeards still be found in the Misty Mountains, and only in this small lonely camp built amongst the crumbled ruins of older, far grander dwarf-holdings. The few brave dwarves who have come here face insurmountable odds in the taming of these wild peaks, however, and the task seems impossible so long as the power of Angmar continues to grow in the far North."; 
