      [2] = {
      ["NAME"] = "Hríthur's Haven"; 
      ["LORE"] = "Once this ruin led into a haven used by dwarf-hunters and scouts who roamed the Misty Mountains in their incessant battles against the goblins. Now it lies sealed and abandoned, the sturdy warriors who once manned it long ago driven from the Mountains."; 
      ["POINTS"] = {
