      [3] = {
      ["NAME"] = "Iskeld's Look-out"; 
      ["LORE"] = "Iskeld's Look-out is one of the oldest dwarf-outposts in the northern Misty Mountains. It was built to stand guard over the source of the river Bruinen and ensure that no enemy would be able to interfere with the life-giving flow of waters down into the lands below. After the fall of the Longbeard's fortress to the south, Iskeld's Look-out was abandoned, and only wild and terrible beasts roam here now."; 
      ["POINTS"] = {
