  -- Misty Mountains / Where Giants Dwell
  [1879071653] = {
      [1] = {
      ["NAME"] = "The Lornstone"; 
      ["LORE"] = "In giantish terms, the Lornstone would likely be described as a quaint villa or summer cottage. As far as anyone else is concerned, it's a grim pile of stone with a commanding overlook of the valleys that wind below, providing an excellent vantage for its inhabitants to bowl great rocks upon whatever interlopers are foolish enough to wander this far into the Mountains."; 
