  [1879071669] = {
      [1] = {
      ["NAME"] = "The Party Tree"; 
      ["LORE"] = "This enormous tree has long been known as a place where festive occasions can be celebrated by the Hobbits of the Shire. Standing on the outskirts of Hobbiton proper it can be seen for quite some ways, and during festivities it is often hung with dozens of bright streamers and lanterns providing a cheery atmosphere for most any event."; 
      ["POINTS"] = {
