  -- Angmar / Bastions of Hope
  [1879084435] = {
      [1] = {
      ["NAME"] = "Lehmä-koti"; 
      ["LORE"] = "Lehmä-koti is a small Earth-kin village tucked within the depths of Ram Dúath. The forces of Angmar have ignored it for the time being as their armies travel the eastern road through these broken valleys.\n\nShould it be discovered that this tribe of Earth-kin have offered aid and sympathy to the Men of the West, the hammer of Angmar would surely fall upon this place."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.82S"; 
          ["LONG"] = "39.27W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Aughaire"; 
      ["LORE"] = "The Hillmen of Aughaire are playing a dangerous game with the powers of Angmar. As one of the larger and more independent tribes of Hillmen in the region, they have considerable sway with their bretheren in the service of Angmar.\n\nAs long as they continue to convince the Lords of Angmar that they may yet join them, the men of Aughaire are allowed a small breadth of freedom. Nevertheless, their game can only play out so long before Angmar grows impatient and either enslaves or crushes this town as a spot of rebellion upon their flanks."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.34N"; 
          ["LONG"] = "39.65W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Tyrn Lhuig"; 
      ["LORE"] = "This small camp is inhabited by a band of hunters out of Aughaire, and as such harbour some sympathies for the Free Peoples of the Eriador.\n\nLuckily for them, this camp is far too small to have come to the notice of the powers of Angmar...yet."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "1.86S"; 
          ["LONG"] = "33.13W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Gabilshathûr"; 
      ["LORE"] = "The dwarf mining-outpost of Gabilshathûr was unwisely founded shortly before the recent revival of the powers of Angmar.\n\nNow these poor souls are trapped, holding out against hope that they can survive and be rescued before the fist of Angmar falls upon them."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.52S"; 
          ["LONG"] = "26.59W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Gath Forthnír"; 
      ["LORE"] = "A small band of brave souls, including a small contingent of Rangers, has created a well-hidden outpost here on the very doorsteps of Carn Dûm.\n\nFrom here, they send spies to watch the movements of the Enemy's armies and offer aid and rest to those brave and strong enough to lead the fight against the False King."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.80N"; 
          ["LONG"] = "23.98W"; 
        }; 
      }; 
      };
  };
