  -- Angmar / The Road to War
  [1879085026] = {
      [1] = {
      ["NAME"] = "Skathmúr"; 
      ["LORE"] = "The armed camp of Skathmúr is responsible for patrolling the canyons of Ram Dúath, ensuring that it is clear and the road well-maintained so that Angmar's armies may move undisturbed through its length."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.92S"; 
          ["LONG"] = "36.67W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Bail Róva"; 
      ["LORE"] = "The fortress of Bail Róva commands the exit from Ram Dúath as it spills into Angmar. For the armies of Angmar, this is the last major fortress they will pass through before embarking on their campaigns to the south."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.22S"; 
          ["LONG"] = "34.97W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Bail Boglakh"; 
      ["LORE"] = "Bail Boglakh is a major staging camp for the armies of Angmar as they gather from the surrounding lands and move into the south.\n\nEven the inhabitants of this vast encampment avoid the valley to the north, however, and those who question the commands of their superiors are hurled over the cliffs there into the unsettling silence and fog below where their screams are soon cut short by dreadful silence."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.03N"; 
          ["LONG"] = "30.44W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Mor Maudhúl"; 
      ["LORE"] = "This watch-tower overlooks the great sulfur lake of Duvuinen to the south. From here, scouts and commanders can survey the watch-fires of the gathering armies and observe their movements as they draw up their plans."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.13N"; 
          ["LONG"] = "27.73W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Bail Dílas"; 
      ["LORE"] = "This small camp acts as a checkpoint through which travellers and messengers to Carn Dûm must pass before approaching the vast gates of the city."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.05N"; 
          ["LONG"] = "28.06W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Gate of Shadows"; 
      ["LORE"] = "The Gate of Shadows is the vast outer gate of the fortress city of Carn Dûm.\n\nStories abound of the horrors within, but here far from the war the small town settled around the gate might seem almost commonplace were it not for the symbols of Angmar's power upon every street and tower.\n\nThe harsh countenance upon the faces of the guards suggests that they brook no hint of rebellion or expression from the citizenry that runs contrary to the wishes of the False King."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.08N"; 
          ["LONG"] = "29.50W"; 
        }; 
      }; 
      };
  };
