  -- Evendim / Tombs of Evendim
  [1879089144] = {
      [1] = {
      ["NAME"] = "Haudh Eärendur"; 
      ["LORE"] = "The tomb of Eärendur, son of Elendur.\n\nEärendur was the tenth and last of the Kings of Arnor - after his death the kindgom of Arnor was divided by his contending sons into the kingdoms of Arthedain, Cardolan and Rhudaur. It was during this period that the capital was moved to Fornost, and the old city of Annúminas was finally abandoned after declining in importance for many years."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.69S"; 
          ["LONG"] = "66.62W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Haudh Elendur"; 
      ["LORE"] = "The tomb of Elendur, son of Valandur. As the ninth king of Arnor Elendur had one of the longest and most enduring reigns of all, ruling over the people of Arnor for one hundred and twenty five years."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.30S"; 
          ["LONG"] = "66.02W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Haudh Valandur"; 
      ["LORE"] = "The tomb of Valandur, son of Tarondor. History mentions relatively little about the eighth king of Arnor, except that he is said to have died in violent and mysterious circumstances sometime around the 50th year of his reign - a foreboding sign perhaps, of the strife that was to fall upon the Northern Kingdoms in the years to come."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.94S"; 
          ["LONG"] = "66.63W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Haudh Elendil"; 
      ["LORE"] = "The tomb of Elendil, son of Amandil. Elendil was the first and founding King of Arnor, whither he had fled into Middle-earth from the great ruin of Númenor. Elendil fought alongside Gil-galad as the leader of the Last Alliance and died during the Siege of Barad-dûr at the hands of Sauron.\n\n After his death Elendil was secretly interred at Halifirien far to the south, but some of his most important standards and personal effects were symbolically placed within the tomb at Annúminas in his stead.."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.67S"; 
          ["LONG"] = "66.97W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Haudh Tarondor"; 
      ["LORE"] = "The tomb of Tarondor, son of Tarcil. The reign of the seventh king of Arnor was marked by a time of comparative peace, for the strife that would consume and in time destroy the Kingdoms of the North had not yet arisen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.87S"; 
          ["LONG"] = "66.14W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Haudh Eldacar"; 
      ["LORE"] = "The tomb of Eldacar, son of Valandil. Eldacar ruled as the fourth king of Arnor, and was the direct grandson of Isildur himself. It is believed that Eldacar ruled during a time of peace that extended from the defeat of Sauron in the War of the Last Alliance at his grandfather's hand, but most of the records of this time in Arnor have been destroyed."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.30S"; 
          ["LONG"] = "67.19W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Haudh Tarcil"; 
      ["LORE"] = "The tomb of Tarcil, son of Arantar. Tarcil was the sixth king of Arnor and his reign lasted for about eighty years before the crown passed to his son, Tarondor."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.13S"; 
          ["LONG"] = "66.02W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Haudh Arantar"; 
      ["LORE"] = "The tomb of Arantar, son of Eldacar. Though the reign of the fifth King of Arnor was said to last for nearly a century, most of the records of this time were lost in the eventual fall of the Northern Kingdoms, and little is now known of his rule."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.02S"; 
          ["LONG"] = "66.54W"; 
        }; 
      }; 
      };
  };
  -- Evendim / Ruins of Evendim
  [1879089145] = {
      [1] = {
      ["NAME"] = "Tham Nambarth"; 
      ["LORE"] = "Several of the great families of Annúminas constructed palatial estates upon the rolling hills of the Evendim Fields, displaying their wealth and prosperity far and wide and providing them summer estates from which they might oversee their extensive holdings of rich croplands.\n\nThough not as extravagant as some of the estates built later, Tham Nambarth was the oldest and closest to the capital and thus considered the most prestigious of all, save for that of the royal family itself at Tinnudir."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.05S"; 
          ["LONG"] = "65.56W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Tham Varan"; 
      ["LORE"] = "The estate of Tham Varan once belonged to one of the oldest and most prestigious families of Annúminas. They were known for a long and, at times, strange contest for influence in the royal courts of Arnor against their arch-rivals who lived near or at Tham Nambarth.\n\nFrom this contentious situation arose a number of favoured tales of intrigue, forbidden love, and all manner of noble improprieties that have titillated the common folk for centuries since."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.10S"; 
          ["LONG"] = "63.86W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Ost Heryn"; 
      ["LORE"] = "Originally built as a fortress to oversee the approach to the Fields of Evendim from the east, Ost Heryn was allowed to languish during the long periods of peace that characterized those times. It was eventually purchased by a newly prosperous family of merchants and converted into a luxurious estate. Alas, the fortunes of Ost Heryn were sorely treated in later years when threats arose unlooked for from the East and it became a rich target for raiders and bandits."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.20S"; 
          ["LONG"] = "61.32W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tham Andalath"; 
      ["LORE"] = "Constructed during the height of Annúminas' influence and wealth in the North, the estate of Tham Andalath became the largest in the Evendim Fields. It quickly became known as a symbol of upstart extravagance and was known for its excessively lavish events and its unabashed influence peddling amongst the lesser houses."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "9.57S"; 
          ["LONG"] = "63.23W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Tham Ornen"; 
      ["LORE"] = "The smallest of the great houses of Arnor built their estate here at Tham Ornen. While they could not claim the wealth or power of the other houses, it is said that this family had close ties with the Royal line of the Kings of Annúminas. As such they suffered few of the more difficult turns of fate that befell many of the other houses, and were highly respected throughout their long history.\n\nNevertheless, their fate became inextricably twined with that of the royal line so as the power of Arnor fragmented and faded, so too did the family at Tham Ornen fade into the pages of history."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.75S"; 
          ["LONG"] = "66.56W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Barad Rath"; 
      ["LORE"] = "Constructed before the original founding of Ost Forod, Barad Rath once stood as the furthest northern frontier in the region of Lake Evendim. Once Ost Forod rose to pre-eminence in the north Barad Rath declined in importance and is believed to have been used as an occasional retreat for members of the Royal Family who could not find sufficent peace and isolation in the bustling courts of Annúminas or their estate at Tinnudir."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.31S"; 
          ["LONG"] = "66.62W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Rantost"; 
      ["LORE"] = "Given the evidence of the beautiful and extensive structures once built on the island of Rantost, it is strangely devoid of mention in the histories of Arnor. It is possible that some powerful family seeking a more reclusive and quiet lifestyle far from the continual chaos of the capital chose to construct their estate here - but if so that same reclusive nature has erased any mention of them from the tales and histories of the Northern Kingdom, and they are lost to time forever."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.35S"; 
          ["LONG"] = "69.61W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Tollobel"; 
      ["LORE"] = "Isolated by the lake from most of the other estates and ruins of Evendim, few scholars are certain of the nature of the ruins that lie there today. The foremost theories suggest that it may have once been either a prison or vault used by the rulers of Annúminas - this at least would explain the general secrecy and rare mention of the place in histories of the region.\n\nAlas, there is little solid evidence to support these ideas, save for a series of unusually deep and extensive cellars within the crumbling ruins. If there were vaults here, they have long ago been picked over for any valuables they might have contained."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.38S"; 
          ["LONG"] = "71.95W"; 
        }; 
      }; 
      };
  };
  -- Evendim / Wilds of Evendim
  [1879089146] = {
      [1] = {
      ["NAME"] = "Nan Orngon"; 
      ["LORE"] = "Nan Orngon is a great stone outcropping discovered by the original settlers of Evendim long ago. While its position overlooking the paths into the Fields of Fornost might have afforded it some importance its steep sides proved impossible to build upon even for the skilled dwarf-masons of the time and it was eventually abandoned to the wilds."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.67S"; 
          ["LONG"] = "61.76W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Tûm Fuin"; 
      ["LORE"] = "The valley of Tûm Fuin and its surroundings are known to be a favourite haunt of local trappers and hunters  out of Ost Forod. It lies well off the beaten path and has an abundance of local wildlife - but there is also little recourse or chance of rescue for any hunter unfortunate enough to be injured while travelling so far afield."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.38S"; 
          ["LONG"] = "65.24W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "The Even-rills"; 
      ["LORE"] = "The Even-rills are a series of connected pools and falls fed by ice melting high in the mountains north of Lake Evendim. They are as frigid as ice even on the warmest days of summer, and bands of rugged North-men have been known to challenge each other to foolhardy contests of endurance to see how long each can withstand the bone-chilling temperatures in the upper pools."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.43S"; 
          ["LONG"] = "69.21W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "The Eavespires"; 
      ["LORE"] = "Few hunters or trappers come this far west of Ost Forod - tales speak of trees that walk and bears that build houses in these remote wilds. While there is little truth to most of these fanciful tales, there is certainly an aura of strange enchantment about the secluded grove called the Eavespires...."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.09S"; 
          ["LONG"] = "71.96W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "The Eave-mere"; 
      ["LORE"] = "The Eave-mere represents the lower stretch of the largest tributary to lake Evendim. Fed by rapids from higher up the mountain and crashing down in great falls towards the lake below, the mere itself is a calm and placid place that was said to be much admired by travellers seeking quiet solace in the old days of Annúminas before the city was abandoned and many of the further shores of Evendim were reclaimed by the wilds."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.83S"; 
          ["LONG"] = "72.78W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Swiftbrook"; 
      ["LORE"] = "This cold and isolated stream cuts deeply through the rock of the mountains high above the western side of Lake Evendim before descending through tumultuous rapids to the Eave-mere below and thence to Lake Evendim itself.\n\nLegends speak of a beautiful maiden who - distraught over the abandonment of her ancestral home and the encroachment of the wilds upon the once glorious city of Annúminas - came here and threw herself into the waters from the high walls of the chasm above.\n\nAlas, even the old stories do not guess at the truth of this tale, or whom the woman might have been."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.97S"; 
          ["LONG"] = "74.61W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "The Twisted Grove"; 
      ["LORE"] = "The Twisted Grove is a dark and forboding place, set in the deepest wilds west of Lake Evendim. Even during the reign of the Kings of Arnor, it was considered an unlucky place and was avoided by most. Now it seems certain that an unwholesome power is at work here, and the very trees seem to radiate a malice towards living things that intrude upon their dark solitude."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.93S"; 
          ["LONG"] = "74.86W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Rushingdale"; 
      ["LORE"] = "Though visited infrequently even in the time of Arnor, the pool now known as the Rushingdale was held in reverence by the local people who would leave offerings floating upon the edge of the waters.\n\nDespite the roar of the nearby falls, a sense of uplifting calm does seem to settle upon your spirit as you gaze into the shifting reflections in the water here."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "13.75S"; 
          ["LONG"] = "75.84W"; 
        }; 
      }; 
      };
  };
