  -- Moria / The Great Delving
  [1879140051] = {
      [1] = {
      ["NAME"] = "Durin's Threshold"; 
      ["LORE"] = "The threshold of the western gate of Khazad-dûm, it was here that Durin completed his legendary journey through the heart of the mountains and came once again into the light of the sun, having discovered the place that would become the home of his peoples for ages to come."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.00S"; 
          ["LONG"] = "115.84W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Dolven-view"; 
      ["LORE"] = "The Dolven-view acts as a window to the Great Delving below. From here, dwarves could once be seen bustling to and fro, visiting the markets, libraries, and workshops of this once-lively cavern. Now there is only a long, lonely view of goblin-fires and vile insects crawling through the dark."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.36S"; 
          ["LONG"] = "112.14W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "The Dwarf-lords' Gate"; 
      ["LORE"] = "As the northern gate of the Great Delving, the Dwarf-lords' Gate provides the main passage for those seeking to reach Durin's Way."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.43S"; 
          ["LONG"] = "111.97W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "The Lonely Span"; 
      ["LORE"] = "The Lonely Span lies just beyond the eastern gate of the Great Delving. It is the only direct passage to the streets of Zelem-melek, and is where the nobles of Khazad-dûm once resided."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.56S"; 
          ["LONG"] = "109.72W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "The Stone Council"; 
      ["LORE"] = "The great carvings on the walls of this ancient grotto represent the ancient dwarf-council that advised Durin and aided in the daily administration of Khazad-dûm. Many of these great carvings have been damaged by time and wear, though luckily the bridge that spans this space remains intact, even if somewhat perilous."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "9.00S"; 
          ["LONG"] = "112.09W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Gazatmur"; 
      ["LORE"] = "The grand plaza of Gazatmur was once the heart of the Great Delving, surrounded by some of the most prestigious institutions and finest architecture within Khazad-dûm. Now it has been overrun by a large goblin encampment that prevents the dwarves from regaining control of the Delving."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.61S"; 
          ["LONG"] = "111.39W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Shemeldurj"; 
      ["LORE"] = "Shemeldurj was an unusual estate constructed by an eccentric but famous architect of the late Second Age. In a departure from classical dwarf-design, it features fanciful spans and buttresses that challenge the observer to seek around each turn and corner to discover where next it goes. Much of it lies in ruins, spoiled by time and the ravaging hands of goblins."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "1.50N"; 
          ["LONG"] = "128.21W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "The Palace of Náin"; 
      ["LORE"] = "After the fall of Durin, terror grasped the dwarves of Moria, now seized by panic and fear. Good Náin took his father's place and strove to quell the menace that threatened his halls, wishing to restore peace to his brethren. His short reign was a valiant one, but he too fell to the Bane of Khazad-dûm."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.67S"; 
          ["LONG"] = "113.33W"; 
        }; 
      }; 
      };
  };
