  -- Moria / The Flaming Deeps
  [1879141031] = {
      [1] = {
      ["NAME"] = "Anazârmekhem"; 
      ["LORE"] = "Anazârmekhem stands as the gate-house to the Flaming Deeps. While its purpose long ago was mainly a matter of governance, its purpose now is as a rallying point for the dwarf-expedition as it reaches into the lowest caverns of Moria."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "13.35S"; 
          ["LONG"] = "108.10W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "The Burning Stair"; 
      ["LORE"] = "Travellers passing from the Water-works into the Flaming Deeps must descend the great Burning Stair. Once the flames were kept tame and well-confined within the furnaces and forges of the dwarves of Khazad-dûm, but under the brutal care of the Orcs, the waters here have become contaminated with foul oils and sulphur, and gouts of fire may leap from every chasm and by-way."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.80S"; 
          ["LONG"] = "110.61W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "The Cross-roads of Ash"; 
      ["LORE"] = "The Cross-roads of Ash -- once it was a great light at the centre of the vast dwarf-forges -- now its light is overwhelmed by the ruddy burning hue of flames and the eternal black pall of soot that hangs over everything here."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.80S"; 
          ["LONG"] = "109.58W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Gate of the Seven Fathers"; 
      ["LORE"] = "Dwarf-legends speak of the Seven Fathers of the Dwarves, whom they claim awoke from the stone first among all the people of Middle-earth - though the Elves claim that they were the first to walk beneath the stars. Here the Seven Fathers have been immortalized in a vast work of Stone-craft."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.80S"; 
          ["LONG"] = "108.56W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Hadâd-mezer"; 
      ["LORE"] = "Hadâd-mezer was renowned amongst the dwarves as one of the greatest workshops of Moria. From these forges came some of the finest works of smith-craft that the world shall ever see."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.72S"; 
          ["LONG"] = "107.65W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Hurmulkezer"; 
      ["LORE"] = "Hurmulkezer once stood as the palace of an ancient dwarf-lord of Moria, though his name has now been lost from the pages of history. Now it stands as a bastion of corruption and evil from which Orc-captains oversee the labours of their minions and slaves in the hot forges of the Flaming Deeps."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.96S"; 
          ["LONG"] = "107.09W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Hudnul-meden"; 
      ["LORE"] = "The gates of Hudnul-meden are said to have sealed away some of the most well-protected vaults of the dwarves in ancient days, but the old texts are not clear on whether they were meant to keep thieves and intruders out, or something else in...."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.84S"; 
          ["LONG"] = "109.58W"; 
        }; 
      }; 
      };
  };
  -- Moria / The Redhorn Lodes
  [1879141032] = {
      [1] = {
      ["NAME"] = "The Orc-watch"; 
      ["LORE"] = "The Orc-watch has been founded by Brogur and Bósi's dwarf expedition as a watch-post over the Orc horde that holds the Redhorn Lodes. The dwarves hope to cut the different Orc tribes off from each other by controlling such key points throughout Moria, but the odds stand brutally against them."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.17S"; 
          ["LONG"] = "106.71W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "The Gate of Ruin"; 
      ["LORE"] = "The Gate of Ruin opens upon the Endless Stair of Khazad-dûm, but as its name now suggests, this doorway into the stairs has been shattered and lies in ruin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.20S"; 
          ["LONG"] = "103.88W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Sigin-tharâkh"; 
      ["LORE"] = "Sigin-tharâkh is little more than a platform jutting up above rivers of molten fire that have broken this section of the caverns into a set of treacherous stone formations. The dwarves of old Moria tied them together with a series of stairs and pathways, but these have not been maintained for hundreds of years, and can be perilous to an unwary traveller."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.10S"; 
          ["LONG"] = "101.06W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "The Tailing Pit"; 
      ["LORE"] = "This vast natural pit was used for hundreds of years by the dwarves of Khazad-dûm to dispose of the tailings left behind by their enormous mining efforts within the Redhorn Lodes. It speaks to the enormous depth of this shaft that in all that time, its bottom yet remains unseen and unsounded."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.32S"; 
          ["LONG"] = "105.83W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Menem-mezel"; 
      ["LORE"] = "This important looking building must once have been an administrative centre for the mining efforts of the dwarves in the Redhorn Lodes. Now, naturally, its interior lies ruined and abandoned, for the Orcs have little use for it."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.25S"; 
          ["LONG"] = "105.69W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Malmezel"; 
      ["LORE"] = "Malmezel was once a street of some sort lined with small buildings. Few of the old records speak to its purpose, though one might guess that it once was filled with workshops or taverns for the dwarves who once worked these mines."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.32S"; 
          ["LONG"] = "106.23W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Ashpar's Command"; 
      ["LORE"] = "In the time of ancient Moria, this building would have served as the quarters of the high-foreman of the Redhorn Lodes; now it acts as the throne of whatever Orc has risen to power over his bretheren in this vast cavern."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.41S"; 
          ["LONG"] = "103.54W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Budkhul-beken"; 
      ["LORE"] = "Budkhul-beken provides a commanding view of the great cavern below. Perhaps in days long past, the mine's overseers stood here to plan out their excavations; now all one can see are the shattered ruins below, crawling with Orcs...."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.82S"; 
          ["LONG"] = "103.15W"; 
        }; 
      }; 
      };
  };
  -- Moria / Zelem-melek
  [1879141033] = {
      [1] = {
      ["NAME"] = "The Twenty-first Hall"; 
      ["LORE"] = "In times long past, a great market stood here where the dwarves of Moria and travellers from abroad would trade in goods from across much of Middle-earth. Fine woods, wine, and foodstuffs would be exchanged for gold, priceless gems, and finely wrought pieces of smith-craft. Anything at all could be bought or sold within this very hall, though it is hard to imagine to look upon it now."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.79N"; 
          ["LONG"] = "119.16W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Mudmul-charâf"; 
      ["LORE"] = "Founded as the dwarves expanded east from the halls of Old Moria to the west, the terraces of Mudmul-charâf once housed many a fine dwarf-craftsman -- rather, at least among those wealthy enough to live but a few halls from the very throne of Durin himself."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.39S"; 
          ["LONG"] = "108.79W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Hall of Flowing Water"; 
      ["LORE"] = "Water has ever been an important aspect of dwarf-artistry, as hidden rivers and lakes are among the most vital features of any settlement beneath the hills and mountains. The Hall of Flowing Water is a fine example of this artistry, and it has remained surprisingly intact after so many years untended."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "9.02S"; 
          ["LONG"] = "107.74W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Gabil-mamach"; 
      ["LORE"] = "The original purpose of the Chamber of Gabil-mamach cannot be found in the old texts and histories of Moria. The simple span that crosses this room does not seem to serve any vital purpose, and the wise might argue whether it is symbolic in nature, or whether something of import was once contained in the space below."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "9.02S"; 
          ["LONG"] = "107.17W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Chamber of Mazarbul"; 
      ["LORE"] = "It is likely that the Chamber of Mazarbul once served some great purpose in the old days of Moria, yet now it is left the tomb of Balin, son of Fundin and friend to old Bilbo, who not so many years ago led an expedition much like the current one to retake Moria and restore it to the glories of old. Alas that his efforts came to ruin so final that none ever returned to their homes to tell the tale of it, and only now has the truth been learned."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "22.85N"; 
          ["LONG"] = "119.25W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Gabil-hul"; 
      ["LORE"] = "Here at Gabil-hul once stood a dam constructed to capture the waters that flow through this area and so supply water to the dwarves inhabiting Zelem-melek. Now it lies in shattered ruins among stagnant puddles."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "9.70S"; 
          ["LONG"] = "108.67W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "The Great Hall of Durin"; 
      ["LORE"] = "Within this grand hall stood the throne of Durin the Deathless, first and greatest father of the dwarves. Though the first Durin lived a life far beyond the span of any other, he did in time pass away, but always another took up his name and ruled on as Durin. The dwarves claim that these rulers are each Durin himself, returned to them from the stone time and again to sit upon the throne of Moria and watch over them, until at last he fell before the power of Durin's Bane, leaving the throne empty, perhaps forever."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.56S"; 
          ["LONG"] = "105.74W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Uzbad-bakân"; 
      ["LORE"] = "The shattered plaza of Uzbad-bakân likely hosted the shops of dwarf-merchants once, long ago. Now it lies mostly silent and strewn with rubble, broken only by the occasional echoing snarl of an Orc."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.79S"; 
          ["LONG"] = "108.38W"; 
        }; 
      }; 
      };
  };
  -- Moria / Eastern Durin's Way
  [1879141034] = {
      [1] = {
      ["NAME"] = "Jazârgund"; 
      ["LORE"] = "The ceremonial chamber of Jazârgund has seen so many rites and pronouncements throughout the ages of Moria that many dwarves claim that Durin's voice is engraved into the very stone from which it is hewn, and can be heard echoing through the vaulted space when all else lies silent. Now one of the dwarf-expedition groups has set up an encampment within the chamber, thus hoping to gain control of the eastern halls of Durin's Way, and the clamour of their preparations is ever-present."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.61S"; 
          ["LONG"] = "105.99W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Skoiruzg"; 
      ["LORE"] = "The nest of Skoiruzg echos with the cries of the merrevail. While this vast cavern was preserved by the dwarves in its natural state for ages past, this place has now been defiled by the presence of these evil creatures, who fall upon any who dare to explore within."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.63S"; 
          ["LONG"] = "99.58W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Hadâd-dûm"; 
      ["LORE"] = "Once the estate of some great dwarf-family of old, Hadâd-dûm now lies in ruin, inhabited by Orcs - or worse."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.01S"; 
          ["LONG"] = "104.61W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Fehem-dûm"; 
      ["LORE"] = "Fehem-dûm was once inhabited by a dwarf-family renowned for its scholars and mastery of the lore of stone and mining. Alas, most of this lore was irretrievably lost along with the bloodline of that family when Khazad-dûm fell into darkness, and the halls of their ancient manse have long since been stripped bare by the ravages of the Orcs."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.17S"; 
          ["LONG"] = "104.54W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Tith-maudhûl"; 
      ["LORE"] = "The merrevail have little love for each other, and each nest guards its territory jealously from the others. It is no different here in the depths of Moria, and no one queen has risen dominant above the others for long. Whether the arrival of the dwarves will prove a strong enough goad to unite them has yet to be seen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.72S"; 
          ["LONG"] = "101.75W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Gríshurbhrum"; 
      ["LORE"] = "This high perch has long been used by merrevail queens to challenge each other to battles of ascendance and rule over each other's broods. So much evil blood has been spilled here over the centuries of darkness that its stains may never be washed from the stone...."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.99S"; 
          ["LONG"] = "100.77W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Khulturg"; 
      ["LORE"] = "This deep black pool once served the dwarves of Moria as a place for the silent contemplation of the depths. Now it lies polluted and the air is rent by the raucous cries of the merrevail flocks. There is no peace to be found here now."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.63S"; 
          ["LONG"] = "100.04W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Dâr-rukh"; 
      ["LORE"] = "The stone cages of Dâr-rukh serve as larders for the merrevail. Captives snatched from the outside world are often brought here, beyond any hope or flicker of light, to be devoured at leisure."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.08S"; 
          ["LONG"] = "100.82W"; 
        }; 
      }; 
      };
  };
  -- Moria / Western Durin's Way
  [1879141035] = {
      [1] = {
      ["NAME"] = "The Chamber of the Cross-roads"; 
      ["LORE"] = "This ancient guard room controls the passages leading up into Durin's Way, as well as the ones east, towards the Twenty-first Hall. It is now occupied by part of the dwarf expedition to re-take Moria from the Orcs and goblins."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.09S"; 
          ["LONG"] = "112.08W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "The Hall of the High Stair"; 
      ["LORE"] = "These stairs lead to the upper halls of Durin's Way. They were likely built before the larger road to the west was completed, and the room in which they were constructed may have been part of the original course Durin mapped through Moria as he explored its depths."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.68S"; 
          ["LONG"] = "111.96W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Gloku-ru"; 
      ["LORE"] = "These ruined workshops and dwellings once housed a family of dwarves who were famous for their skill in crafting wonderous toys in intricate designs of wood and metal. Such simple joys were lost long ago when darkness fell upon Khazad-dûm, and the Orcs came."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.08S"; 
          ["LONG"] = "111.20W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Ninknakh Faltor"; 
      ["LORE"] = "Whatever its original purpose was, a large host of goblins has made its residence in the hall of Ninknakh Faltor. Their dark shrines and crude constructions litter the area, and their sheer numbers pose a serious threat to any hopes of retaking Durin's Way."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.56S"; 
          ["LONG"] = "112.87W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Tharâkh Bazân"; 
      ["LORE"] = "Tharâkh Bazân is a most unusual wonder to find beneath the roots of a mountain. It is more commonly known as 'Durin's Garden', for Durin himself ordered it built so that any dwarf might come here and be reminded of the wider sun-lit world beyond the bounds of Moria. Clever constructs of polished metal and crystal guide light down from far above to grant life here, beneath the mountain. It seems that the evil things that now infest Moria likewise find this light uncomfortable, for among all the works of the dwarves, Tharâkh Bazân remains relatively unmarred by their ravages."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.60S"; 
          ["LONG"] = "109.15W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Salab Nurjundul"; 
      ["LORE"] = "Salab Nurjundul was once one of the busiest intersections in all of Moria, and each day hundreds if not thousands of dwarves would pass through here going about their lives and business. Now it is desolate and empty, offering only a dangerous gauntlet for would-be explorers to pass through with constant threat of ambush by the evil denizens who befoul the once-beautiful workshops and residences nearby."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.61S"; 
          ["LONG"] = "107.46W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Uflûmp-mur"; 
      ["LORE"] = "These chambers once acted as the gate-house of the Endless Stair. It is from here that one might once have made the ascent up thousands of steps to the heights of Zirak-zigil above and thereupon mounted the Tower of Durin, and from there look out upon the wide world below with an eye rivalling that of the Eagles themselves. Now Orcs act as the wardens of this place, and they are loath to allow any to pass uncontested."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.08S"; 
          ["LONG"] = "110.26W"; 
        }; 
      }; 
      };
  };
  -- Moria / Nud-melek
  [1879141036] = {
      [1] = {
      ["NAME"] = "The First Hall"; 
      ["LORE"] = "The First Hall was the original entrance to Khazad-dûm, where Durin first set foot within the caverns that would one day become the greatest city of the dwarves. Moria was indeed the most enduring city of any people in the long history of Middle-earth, until at last it fell and the end came, as it must to all things."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.00S"; 
          ["LONG"] = "96.08W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Khadar-zarâm"; 
      ["LORE"] = "This vast array of stairs allows one to descend from the seat of Durin's power to the greatest hall in all of Khazad-dûm. Originally a vast natural cavern supported by massive pillars of stone, over the long years the dwarves gradually worked it into a wonder of craftsmanship, with a forest of pillars carved into great trees supporting the weight of an entire mountain above."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.08S"; 
          ["LONG"] = "103.65W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "The Deep Cross-road"; 
      ["LORE"] = "This junction between the vast hall in Nud-melek and the chamber of Mazarbul farther to the west seems unimportant in the vastness of Moria, but for some reason Mazog's Orcs guard it with a keen vigilance and attack all passersby therein."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.29S"; 
          ["LONG"] = "101.13W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Khurjezer"; 
      ["LORE"] = "Khurjezer appears to be the only remaining bridge across the lesser chasm that has rent the great hall of Nud-melek. This chasm does not appear in the ancient maps of Khazad-dûm, and it is possible that an earthquake ripped it apart in the years shortly before the appearance of Durin's Bane."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.28S"; 
          ["LONG"] = "100.90W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "The Bridge of Khazad-dûm"; 
      ["PROGRESS"] = "Find the Bridge of Khazad-dûm (west arch)"; 
      ["LORE"] = "The bridge of Khazad-dûm was conceived as an ancient defence designed to deter any who might seek to invade the city. The chasm it crosses is among the largest in Moria, and has been here since the city was founded so many long years ago. Its depths seem wholly bottomless to the reckoning of a mortal eye. Now the bridge lies in ruins, its western span jutting forlornly out over the depths of the chasm, the very stone shattered and charred by unknown forces."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.00S"; 
          ["LONG"] = "99.19W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "The Bridge of Khazad-dûm"; 
      ["PROGRESS"] = "Find the Bridge of Khazad-dûm (east arch)"; 
      ["LORE"] = "The Bridge of Khazad-dûm has stood over this great chasm for many an age, but does so no more. The eastern span now reaches out like a finger pointing into the darkness beyond. It seems that whatever destroyed the bridge it fell not so long ago, as flakes of rock can still be seen to occasionally crack away from the shattered tip to fall away into the chasm, and fresh arrows pepper the stonework."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.00S"; 
          ["LONG"] = "98.55W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Sudulthurkh"; 
      ["LORE"] = "This great course of stone offers a path to the upper caverns that could be traversed -- albeit with some difficulty -- by waggons or loaded vehicles. The path below also offers the only alternate road into Moria now that the Bridge of Khazad-dûm lies ruined."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.56S"; 
          ["LONG"] = "99.15W"; 
        }; 
      }; 
      };
  };
  -- Moria / The Water-works
  [1879141037] = {
      [1] = {
      ["NAME"] = "The Rotting Cellar"; 
      ["LORE"] = "The brackish damp and mould of the deep Water-works have led the dwarves of the expedition to name this place the Rotting Cellar. The air seems laden with an unhealthy mildew, though the view offered over the once-great Water-works of Moria remains awe-inspiring."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.97S"; 
          ["LONG"] = "111.97W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "The Great Wheel"; 
      ["LORE"] = "This massive structure once powered great pumps that moved water about Khazad-dûm, providing drinking water for the dwarves, as well as supplying the enormous quantities of water needed to cool the grand forges. Nowhere else in Middle-earth could such feats of engineering be found, but, as they have been left unmaintained, most of these have fallen to ruin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.39S"; 
          ["LONG"] = "115.60W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Durin's Beard"; 
      ["LORE"] = "Several underground rivers were discovered flowing throughout the great caverns that make up Khazad-dûm, and all of these were tamed by the dwarves over the centuries. Here, the greatest of these torrents have been brought together to form the great waterfall that came to be known as Durin's Beard, in honour of the Father of the Dwarves."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "13.65S"; 
          ["LONG"] = "112.61W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "The Lost Palace"; 
      ["LORE"] = "Whether this dilapidated structure was ever truly a palace is hard to say. Time and moisture have taken their tolls, and anything of value was either stolen or rusted away long ago. It might have once housed the dwarves responsible for administrating the great Water-works that stretch out of sight from the steps of this building."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.04S"; 
          ["LONG"] = "114.95W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "The Chamber of the Pool"; 
      ["LORE"] = "Whatever this place once was, or whatever it contained, it now holds only stagnant foetid pools and debris. It may once have served as the entrance to an ancient store-house."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.47S"; 
          ["LONG"] = "115.31W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "The Chamber of Dark Waters"; 
      ["LORE"] = "This ancient store-house may once have held materials needed to maintain the great mechanisms of the Water-works of Khazad-dûm. Now it lies quiet, filled with still black water that does not appear to have been stirred by any living thing for years."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.97S"; 
          ["LONG"] = "111.88W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "The Lost Treasury"; 
      ["LORE"] = "The surviving records of Khazad-dûm mark this old store-house as a treasury. Perhaps this building once overflowed with dwarf-gold and jewels, but that was long ago, before the Orcs came and stole it all away as tribute for the Dark Lord of Mordor."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.33S"; 
          ["LONG"] = "110.97W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Narag-kheleb"; 
      ["LORE"] = "Next to this great pillar, in the ceiling far, far above, one can make out an ancient shaft that seems to lead to the upper levels of Moria. Whatever use the dwarves had for it, it seems that the goblins have since taken to using it as a place to hurl their refuse."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.17S"; 
          ["LONG"] = "114.67W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Harâzgund"; 
      ["LORE"] = "The gateway of Harâzgund leads to the source of one of the greatest ducts in all of Khazad-dûm. It once supplied water to nearly half of all the dwarves who lived beneath these mountains, but it was broken by some unknown calamity during the long dark years after the fall of Durin. Now the water lies here unmoving, flooding the deeps in great stagnant pools."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.60S"; 
          ["LONG"] = "116.93W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "The Chamber of Memory"; 
      ["LORE"] = "Dwarf-scholars believe that the Chamber of Memory was so named because it held a great repository of books and scrolls, storing many of the more mundane records of Khazad-dûm. If it indeed ever held such records, they have either been stolen away or were destroyed when the Water-works flooded after the kingdom's fall."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.15S"; 
          ["LONG"] = "111.62W"; 
        }; 
      }; 
      };
  };
  -- Moria / The Foundations of Stone
  [1879141038] = {
      [1] = {
      ["NAME"] = "The Shadowed Refuge"; 
      ["LORE"] = "The Shadowed Refuge lies in the deepest extents of Moria. Few records remain to describe these caverns, though scholars presume that it was somewhere within that Durin's Bane lay slumbering for uncounted years before the dwarves delved and awoke its wrath. This area can hardly be called a refuge now, save that a small expedition has killed or driven off the strange creatures from this section of the caverns and stands a vigilant guard against any that approach."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "13.02S"; 
          ["LONG"] = "101.22W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "The Endless Stair"; 
      ["LORE"] = "This stair was rumoured to have been built up through a natural passage that ran from the deepest caverns of Khazad-dûm to the frozen heights of Zirak-zigil, far above. Here at the bottom, parts of it appear to have recently been shattered by some terrible force."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.00S"; 
          ["LONG"] = "99.00W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "The Bridge-shard"; 
      ["LORE"] = "An enormous shard of stone lies half-submerged in this gloomy lake, jutting upwards into the endless darkness. It appears to be a broken remnant of the great Bridge of Khazad-dûm, fallen from far above when the bridge was shattered."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.12S"; 
          ["LONG"] = "97.68W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Zabad-fakâk"; 
      ["LORE"] = "The arches of Zabad-fakâk open upon the Foundations of Stone, the deepest caverns in all of Moria. The dwarves of old long shunned these caverns out of a sense of foreboding and lingering darkness that could never be fully dispelled by lantern or torch, but when rich veins of mithril were discovered within, these concerns were set aside, and the dwarves began to dig in earnest at the very roots of the mountain."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.96S"; 
          ["LONG"] = "101.70W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Dalgum-ru"; 
      ["LORE"] = "The caverns of Dalgum-ru have never been safe. Even in Durin's day, miners who entered these twisted passages would sometimes disappear without trace, and rarely would an expedition pass through without reporting strange shadows moving just beyond the light of torches, but never clearly seen."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.15S"; 
          ["LONG"] = "101.42W"; 
