  -- Moria / Bulwarks of the Enemy
  [1879146846] = {
      [1] = {
      ["NAME"] = "Menem-berej"; 
      ["LORE"] = "The great pit of Menem-berej once held one of the richest lodes of mithril ever discovered in Khazad-dûm, and this enormous platform was constructed to aid the dwarves in their efforts to extract the precious metal. Though this mine was believed to be mostly played out by the time of the Fall of Khazad-dûm, the dwarves of the expedition hope to find some remaining veins to prove the value of this dangerous foray, and to draw more of their bretheren from abroad to aid them in reclaiming their ancient home."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.04S"; 
          ["LONG"] = "112.75W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Harâzgund"; 
      ["LORE"] = "The gateway of Harâzgund leads to the source of one of the greatest waterways in all of Khazad-dûm. It once supplied water to nearly half of all the dwarves who lived beneath these mountains, but it was broken by some unknown calamity during the long dark years after the fall of Durin. Now the water lies here unmoving, flooding the deeps in great stagnant pools."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.60S"; 
          ["LONG"] = "116.93W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "The Forges of Khazad-dûm"; 
      ["LORE"] = "The Forges of Khazad-dûm were without rival, greatest of all forges in the Third Age of Middle-earth. The dwarves will not rest until it has been reclaimed, for much of the priceless artistry born in that hall has been lost to the long years of Moria's abandonment."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.98S"; 
          ["LONG"] = "109.75W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Fil Gashan"; 
      ["LORE"] = "The Orcs of Fil Ghashan are suspiciously watchful, brooding ever on their hopeful assault against Lothlórien. They show no mercy to any who dare threaten their plans."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.07S"; 
          ["LONG"] = "109.58W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "The Gate of Ruin"; 
      ["LORE"] = "The Gate of Ruin opens upon the Endless Stair of Khazad-dûm, but as its name now suggests, this doorway into the stairs has been shattered and lies in ruins."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.20S"; 
          ["LONG"] = "103.88W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Skûmfil"; 
      ["LORE"] = "The Orc-graves in Skûmfil have bred many sickening creatures, all grown strong on the flesh of the dead, and all consumed with evil thoughts."; 
      ["POINTS"] = {
