  -- Moria / In the Footsteps of the Fellowship
  [1879146847] = {
      [1] = {
      ["NAME"] = "Durin's Threshold"; 
      ["LORE"] = "The threshold of the western gate of Khazad-dûm, it was here that Durin completed his legendary journey through the heart of the mountains and came once again into the light of the sun, having discovered the place that would become his people's home for ages to come."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.00S"; 
          ["LONG"] = "115.84W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "The Chamber of the Cross-roads"; 
      ["LORE"] = "This ancient guard room controls the passages leading up into Durin's Way, as well as the ones east, towards the Twenty-first Hall. It is now occupied by part of the dwarf-expedition to reclaim Moria from the Orcs and goblins who now hold sway."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.09S"; 
          ["LONG"] = "112.08W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "The Twenty-first Hall"; 
      ["LORE"] = "The Twenty-first hall is among the largest in all of Khazad-dûm, and often regarded as the most famous. In times long past a great market stood here where the Dwarves of Moria and travellers from abroad would trade in fine goods from across much of Middle-earth. Fine woods and wine and foodstuffs would be exchanged for gold and priceless gems and finely wrought pieces of smith-craft. In truth nearly anything at all could be bought or sold within this very hall, though it is hard to imagine it to look upon it now."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.79N"; 
          ["LONG"] = "119.16W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Chamber of Mazarbul"; 
      ["LORE"] = "It is likely that the Chamber of Mazarbul once served some great ceremonial purpose in the old days of Moria; now it remains the tomb of Balin, son of Fundin and friend to old Bilbo, who, not so many years ago, led an expedition much like the current one to retake Moria and restore it to the glories of old."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "22.85N"; 
          ["LONG"] = "119.25W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "The Bridge of Khazad-dûm"; 
      ["PROGRESS"] = "Find the Bridge of Khazad-dûm (west arch)"; 
      ["LORE"] = "The bridge of Khazad-dûm was conceived as an ancient defence designed to deter any who might seek to invade the city. The chasm it crosses is among the largest in Moria, and has been here since the city was founded thousands of years ago. Now the bridge lies in ruin, its western span jutting forlornly out over the depths of the chasm, the very stone shattered and charred by unknown forces."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.00S"; 
          ["LONG"] = "99.19W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "The Bridge of Khazad-dûm"; 
      ["PROGRESS"] = "Find the Bridge of Khazad-dûm (east arch)"; 
      ["LORE"] = "The Bridge of Khazad-dûm has stood over this great chasm for many a long age, but does so no more. The eastern span now reaches out like a finger pointing into the darkness beyond. It seems that whatever destroyed the bridge fell not so long ago, as flakes of rock can still be seen occasionally cracking away from the shattered tip to fall away into the chasm, and fresh arrows pepper the stonework there."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.00S"; 
          ["LONG"] = "98.55W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "The First Hall"; 
      ["LORE"] = "The First Hall was the original entrance to Khazad-dûm, where Durin first set foot in the caverns that would one day become the greatest city of the dwarves, and indeed the most enduring city of any people in the long history of Middle-earth, until at last it fell into ruin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.00S"; 
          ["LONG"] = "96.08W"; 
        }; 
      }; 
      };
  };
