      };
      [3] = {
      ["NAME"] = "The Archives of Vorondil"; 
      ["PROGRESS"] = "Find The Archives of Vorondil plaque"; 
      ["LORE"] = "Also known as Gobeth Vorondil, named for the last Steward not to serve as Ruling Steward. This library was built when Minas Anor was renamed as Minas Tirith. It is a brighter, more orderly place filled with works penned within the past thousand years, as well as transcripts of major texts from ages past. Sometimes called Gobeth Wain, or the New Archives, though this name has fallen out of favour for obvious reasons with the passage of centuries."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.14S"; 
          ["LONG"] = "18.75W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "The Black House"; 
      ["PROGRESS"] = "Find The Black House plaque"; 
      ["LORE"] = "Also known as Bâr Vorn, named for the dark stone of which it is built, as well for the ink used within. This is the chief scriptorium of Minas Tirith, where scribes write, copy, and translate texts both old and new. It is part of the Houses of Lore, and its tower houses the school's Warden."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "22.35S"; 
          ["LONG"] = "37.87W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Houses of Lore"; 
      ["PROGRESS"] = "Find the Houses of Lore plaque"; 
      ["LORE"] = "Also known as Bair Istoth, a collection of halls and towers where the wise gather to teach, learn, argue, and think. Younger sons and daughters of the nobility are often sent here to become sages; teachers from the Houses also work throughout the city, teaching rich and poor children alike to read and write, as well as numbers, history, and other lore."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "22.28S"; 
          ["LONG"] = "37.92W"; 
        }; 
      }; 
      };
