  -- Vales of Anduin / Enemies of the Vales
  [1879387432] = {
      [1] = {
      ["NAME"] = "Ylgraf"; 
      ["PROGRESS"] = "Explore Ylgraf"; 
      ["LORE"] = "Wargs have often ventured into the Beorning-lands over the years, but those that dwell at Ylgraf today are larger and more ferocious than others of their kind. These wargs come from Gundabad, and they slink among the rocks to the north of the Beorninghús, waiting to attack Grimbeorn and his kin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.76N"; 
          ["LONG"] = "53.60W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Goblin-gate"; 
      ["PROGRESS"] = "Explore the Goblin-gate"; 
      ["LORE"] = "The Beornings keep one eye on the Goblin-gate, for on this side of the mountains it is the primary way into, and out of, Goblin-town. During the last days of the War of the Ring, Orcs and goblins streamed through the Goblin-gate into the Vales of Anduin, raiding and burning peaceful Woodmen villages. Led by Grimbeorn the Old, the Beornings and Woodmen stood against the goblins and finally turned them back.\n\nNow Goblin-town is quiet and there is little activity at the Goblin-gate, but the Beornings have not forgotten. They watch it still."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.62N"; 
          ["LONG"] = "62.78W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Waldfast"; 
      ["PROGRESS"] = "Explore Waldfast"; 
      ["LORE"] = "The peaceful Woodmen village of Waldfast has been destroyed by a goblin-host. Sentries are posted throughout the village, but their numbers are too few to explain the scope of the destruction. A cave behind the village might hold answers."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.35N"; 
          ["LONG"] = "60.23W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Fil Sulmog"; 
      ["PROGRESS"] = "Explore Fil Sulmog"; 
      ["LORE"] = "Following the final battles of the War of the Ring, a company of Orcs has established a tenuous foothold among the hills of Woodsedge. These are the Shapag-hai, and they have discovered they prefer to prey upon traders and travellers rather than warriors and soldiers. From their camp of Fil Sulmog they practice the art of pillaging, and have become quite good at it."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.59S"; 
          ["LONG"] = "4.98W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Tríubad"; 
      ["PROGRESS"] = "Explore Tríubad"; 
      ["LORE"] = "The forester camp of Tríubad provided timber and other necessary supplies with which the Woodmen built their homes and the walls of their villages, but then the Taurogrim came. The Woodmen name them 'Thyrsir,' wood-demons, and they fear their blighted touch.\n\nThe Woodmen know Tríubad may be lost, but they do not spend time in mourning. Instead they seek adventurers to stop the Thyrsir from spreading the blight to other villages in Woodsedge."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.04N"; 
          ["LONG"] = "54.48W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Côs Braen"; 
      ["PROGRESS"] = "Explore Côs Braen"; 
      ["LORE"] = "Côs Braen stands along the southern edge of Laerlad, home to the Ungoledain and their venomous kin. They hate Grimbeorn and his folk with a sickly passion, and would wipe out Beornings and Gauredain alike if they were given the chance."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.59S"; 
          ["LONG"] = "53.48W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Sharkey's Slough"; 
      ["PROGRESS"] = "Explore Sharkey's Slough"; 
      ["LORE"] = "Dredging machines stand in the mud of the Gladden Fields, once manned by Orc and half-orc, but now abandoned. A great hole was delved at Sharkey's Slough, and rumour has it that some prize was found in its depths. The half-orcs remain, seeking further treasures for themselves and not for their master. He remains silent, his thoughts elsewhere, and his servants at the Slough have received no message from him in some time."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.71N"; 
          ["LONG"] = "59.29W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Lúlstrok"; 
      ["PROGRESS"] = "Explore Lúlstrok"; 
      ["LORE"] = "Orcs searched the marshland for many weeks, operating from their camp of Lúlstrok in the north-western reaches the Gladden Fields. One of the great dredging machines stands there now, silent and still, but it once churned the earth during both day and night. Weapons, pieces of armour, and other trinkets were brought to Lúlstrok, but wherever these prizes may have gone in the time since is unknown."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.30N"; 
          ["LONG"] = "61.93W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "The Warrens of Tûn Melen"; 
      ["PROGRESS"] = "Explore the Warrens of Tûn Melen"; 
      ["LORE"] = "What lies within the Warrens of Tûn Melen? Only by venturing within can you know for certain, but soldiers that serve the White Hand might already have done so, searching for treasure if not information."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.63N"; 
          ["LONG"] = "4.47W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Shathur-munzu"; 
      ["PROGRESS"] = "Explore Shathur-munzu"; 
      ["LORE"] = "A wet mist soaks the stones that lead to Shathur-munzu, the southernmost outpost of lost Khazad-dûm. The fortress is broken now, and dwarves have not lived there for many years. Shathur-munzu was the site of one of the bloodiest battles in the War of Dwarves and Orcs, and King Thráin did not hold it long."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "39.77S"; 
          ["LONG"] = "2.40E"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Brafulug"; 
      ["PROGRESS"] = "Explore Brafulug"; 
      ["LORE"] = "Ugúrz established his camp of Brafulug to prey upon traders on the road, believing his Orcs would be far enough from Lothlórien to escape the notice of the Elves. This would prove to be a miscalculation."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "3.44S"; 
          ["LONG"] = "60.98W"; 
        }; 
      }; 
      };
  };
