  -- Vales of Anduin / Protectors of the Vales
  [1879387438] = {
      [1] = {
      ["NAME"] = "Beorninghús"; 
      ["PROGRESS"] = "Explore the Beorninghús"; 
      ["LORE"] = "Home to Grimbeorn the Old and others of his family, the Beorninghús is generally closed to outsiders. During the final weeks of the War of the Ring, the Beornings found it necessary to take a more active role in the protection of the Vales of Anduin.\n\nThe sound of birdsong and the buzzing of dumbledors greet all who do approach the Beorninghús, but if they fail to ask Grimbeorn's permission, those are not the only sounds they will hear; Grimbeorn's wrath will be both loud and swift."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "13.60N"; 
          ["LONG"] = "53.32W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Vegbár"; 
      ["PROGRESS"] = "Explore Vegbár"; 
      ["LORE"] = "The outpost of Vegbár allows the Beornings to keep watch over the road between the High Pass and the Old Ford, and to charge their toll of travellers and traders who go that way. Skalla commands at Vegbár, and despite his gruff exterior he cares deeply for his neighbours and worries for their safety."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.90N"; 
          ["LONG"] = "58.39W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Ainhard's Homestead"; 
      ["PROGRESS"] = "Explore Ainhard's Homestead"; 
      ["LORE"] = "The forester Ainhard keeps a homestead in the Wolf-denes, and his closest companions are his three hounds. He lives in fear of Orcs, goblins, and taurogrim, for he is no warrior. He relies upon the nearby Beorning outpost of Vegbár for protection."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.70N"; 
          ["LONG"] = "59.46W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Hodver's Homestead"; 
      ["PROGRESS"] = "Explore Hodver's Homestead"; 
      ["LORE"] = "Hodver keeps a homestead in the Wolf-denes, but he is ever ready to make for the Beorning outpost of Vegbár at the first sign of Orcs or wargs. 'I am no coward,' he says, 'but I am no fool, either.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.65N"; 
          ["LONG"] = "58.13W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Gudule's Homestead"; 
      ["PROGRESS"] = "Explore Gudule's Homestead"; 
      ["LORE"] = "Gudule keeps a homestead in the Wolf-denes. 'This land has never been safe,' he says. 'We live too near to the mountains to sleep very soundly at night. But this is home.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.04N"; 
          ["LONG"] = "58.87W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Hultvís"; 
      ["PROGRESS"] = "Explore Hultvís"; 
      ["LORE"] = "The Woodmen town of Hultvís was attacked during the closing weeks of the War of the Ring, but the Orcs and wargs were pushed back and eventually defeated by a united force of Beornings and Woodmen at the Battle of the Old Ford. Now the town seeks to rebuild and refortify, and seeks adventurers willing to assist with the recovery effort.\n\nThe Woodmen are a superstitious people, and at Hultvís you are sure to hear many legends and tales. These concern the beautiful and forlorn Maiden of Gladden, the spectral Vagári the Wanderer, hunger-crazed Old Mad Ubb, and the vanished Hól-budlan."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.02N"; 
          ["LONG"] = "54.48W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Blómgard"; 
      ["PROGRESS"] = "Explore Blómgard"; 
