  [1879395348] = {
      [1] = {
      ["NAME"] = "Ram Raedas"; 
      ["PROGRESS"] = "Learn of Ram Raedas"; 
      ["LORE"] = "'It was Ram Raedas, the Ensnaring Wall. We built that fortification athwart the Neck of Iron to keep enemies from crossing through from Udûn and attacking the Great Alliance from the rear.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "96.53S"; 
          ["LONG"] = "131.74W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Díngarth"; 
      ["PROGRESS"] = "Learn of Díngarth"; 
      ["LORE"] = "'Díngarth was the most secure location of the Free Peoples, built at the beginning of the siege and repeatedly assailed and rebuilt over the ensuing years. Near the end it was mostly emptied, kept as a last redoubt in the event that the forward forces of the Alliance were overwhelmed and forced to retreat. My sons greatly enjoyed making life difficult for Ontamo, the master of the fortifications at Díngarth, despite my chiding of them for it.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "97.30S"; 
          ["LONG"] = "131.51W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Echad-in-Edhil"; 
      ["PROGRESS"] = "Learn of Echad-in-Edhil"; 
      ["LORE"] = "'Echad-in-Edhil was the forward encampment of the Elves, and it was where Gil-galad and his household dwelt for much of the siege. From this camp, the High Elves of the Bright Company undertook many dangerous excursions on the plains before Barad-dûr and against Sauron's servants.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "96.23S"; 
          ["LONG"] = "127.69W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Adambel"; 
      ["PROGRESS"] = "Learn of Adambel"; 
      ["LORE"] = "'Adambel was the forward encampment of Men, from whence my father Elendil and my brother Anárion and I commanded our armies. It was the nearest to Barad-dûr we deemed it wise to set camp.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "98.76S"; 
          ["LONG"] = "126.04W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Amon Fuin"; 
      ["PROGRESS"] = "Learn of Amon Fuin"; 
      ["LORE"] = "'It was Amon Fuin, the Gloom-hill, the last remaining major fortification held by Sauron’s forces beyond the Dark Tower itself. The Mordorrim conducted sacrifices and other rituals behind the walls of that fortress, and many were the soldiers of the Great Alliance we lost to their deadly excursions.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "98.91S"; 
          ["LONG"] = "122.01W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Dol Naeg"; 
      ["PROGRESS"] = "Learn of Dol Naeg"; 
      ["LORE"] = "'Dol Naeg was the name of a rocky spur on the north slope of Amon Amarth. It is a place of great sadness for me, for that is where my father died. It is also where I obtained an artifact of Sauron that brought me only sorrow and grief. Would that I had never stood there!' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "97.17S"; 
          ["LONG"] = "118.47W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Halroth"; 
      ["PROGRESS"] = "Learn of Halroth"; 
      ["LORE"] = "'I know the tunnel of Halroth, but I never ventured inside. Glorfindel of the Bright Company knows of that place. Let him speak of it, if he will.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "92.72S"; 
          ["LONG"] = "116.68W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Barad-dûr"; 
      ["PROGRESS"] = "Learn of Barad-dûr"; 
      ["LORE"] = "'Barad-dûr, the tower of Sauron, was built not by his servants but by Númenórean hands. He has bent it to his evil, and it stood as a cruel mockery to my people. He remained within for the long years of the siege, content to outlast us, but it was not to be.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "96.76S"; 
          ["LONG"] = "114.39W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Baur Kasak"; 
      ["PROGRESS"] = "Learn of Baur Kasak"; 
      ["LORE"] = "'The wargs of Baur Kasak were feared by all the men under my command, for the beasts could move at incredible speed across the plains, seizing an unwary soldier with their powerful jaws and charging away in moments. Many of my soldiers insisted upon being accompanied on the plains by an Elf of Gil-galad's forces, for only their swift bows could dissuade the advance of such creatures.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "100.98S"; 
          ["LONG"] = "130.85W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Mokh Shapat"; 
      ["PROGRESS"] = "Learn of Mokh Shapat"; 
      ["LORE"] = "'I am grieved to hear that the Orcs remain still in this world. They were a fearsome foe when we fought against Sauron, cruel and deadly, without an ounce of mercy. Many of my friends and soldiers perished in their encampments. The one called Mokh Shapat remained for years, despite our attempts to eradicate it.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "101.69S"; 
          ["LONG"] = "129.49W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Mokh Darga"; 
      ["PROGRESS"] = "Learn of Mokh Darga"; 
      ["LORE"] = "'Mokh Darga was a large encampment from which the Orcs launched many attacks upon Adambel. My father himself turned away several of these assaults, deeds which inspired not just the men under his command but also proved an inspiration to me and to my brother. Alas for Anárion!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "95.48S"; 
          ["LONG"] = "123.92W"; 
        }; 
      }; 
      };
      [12] = {
      ["NAME"] = "Barthost"; 
      ["PROGRESS"] = "Learn of Barthost"; 
      ["LORE"] = "'Barthost, you say? I do not remember that name. Perhaps Gultháva's enchantment has not yet released me from its grip.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "95.21S"; 
          ["LONG"] = "119.93W"; 
        }; 
      }; 
      };
      [13] = {
      ["NAME"] = "Urshúlagar"; 
      ["PROGRESS"] = "Learn of Urshúlagar"; 
      ["LORE"] = "'It was a camp of evil men, sworn to serve Sauron. I never set foot inside, but I know the forces of Sauron festered in their malice within its bounds, and set forth with hatred in their hearts, intending to defeat the Great Alliance.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "94.14S"; 
          ["LONG"] = "118.84W"; 
        }; 
      }; 
      };
      [14] = {
      ["NAME"] = "Matum Graz"; 
      ["PROGRESS"] = "Learn of Matum Graz"; 
      ["LORE"] = "'The shades and spirits of Matum Graz lingered alongside the dead that walk, and they nursed hatred for the living in their evil hearts. I do not wish to speak of them now.' - Isildur"; 
      ["POINTS"] = {
        [1] = {
