  -- Imlad Morgul / Scouting Minas Morgul
  [1879395353] = {
      [1] = {
      ["NAME"] = "Barad Angarth, the Gate-wardens' Keep"; 
      ["PROGRESS"] = "Explore Barad Angarth, the Gate-wardens' Keep"; 
      ["LORE"] = "Barad Angarth has been reclaimed from the Orcs, and now it serves the White Company as a foothold in the city. Further such towers may better serve the reclamation, but they will first need to be taken from the foes that hold them."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "24.97S"; 
          ["LONG"] = "19.26E"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Barad Orhant, the Tower of the Treegarth"; 
      ["PROGRESS"] = "Explore Barad Orhant, the Tower of the Treegarth"; 
      ["LORE"] = "Barad Orhant once overlooked the Circle of Skill in Minas Ithil, but now the view has changed. That tier is now the Circle of Sorrow, and it is home only to the Dead. So too was the Tower of the Treegarth, but it has been seized for the use of the White Company. Perhaps it will be the same for the Circle of Sorrow."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "26.45S"; 
          ["LONG"] = "19.16E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Barad Elenath, the Star-gazers' Spire"; 
      ["PROGRESS"] = "Explore Barad Elenath, the Star-gazers' Spire"; 
      ["LORE"] = "Barad Elenath, the Star-gazers' Spire, was once a place of study and learning. From its great height the scholars of the city spent night after night studying the moon and the stars, but that was long ago. The merrevail of Lhaereth the Stained claimed this tower, and have only just been driven away. The tower serves now as a foothold for the White Company's reclamation of Minas Morgul."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.19S"; 
          ["LONG"] = "19.31E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Barad Arthir, the Lofty Watch-fort"; 
