  -- Wells of Langflood / Natural Formations in the Wells of Langflood
  [1879405904] = {
      [1] = {
      ["NAME"] = "Tithroz"; 
      ["PROGRESS"] = "Explore Tithroz"; 
      ["LORE"] = "The Floodfells are riddled with caves, but the Beornings who live in this area prefer not to explore their dark, narrow depths. Vile insects, pestilential bats, and burrowers with sharp teeth make their homes in these places, and the folk of Limlók only wish to concern themselves with these unpleasant creatures if they emerge into the sunlight or moonlight."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.86N"; 
          ["LONG"] = "1.47W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Reikfoss of Greylin"; 
      ["PROGRESS"] = "Explore Reikfoss of Greylin"; 
      ["LORE"] = "The great cascade of Reikfoss marks the location where the River Greylin flows out of the Elderslade into Misthallow to form one of the falls of the Three Sisters. Together, the vapours that rise from the base of Reikfoss and those of the Twainmantle blanket all of Misthallow in fog."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "31.85N"; 
          ["LONG"] = "60.03W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Twainmantle"; 
      ["PROGRESS"] = "Explore the Twainmantle"; 
      ["LORE"] = "The two waterfalls of the Twainmantle are formed at the passage of the Langwell and a smaller stream out of Elderslade into Misthallow. Together with the Reikfoss, these three falls make up the Three Sisters, the combined vapour that rises from their bases enshrouding all of Misthallow in fog."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "31.44N"; 
          ["LONG"] = "62.75W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Veithhól"; 
      ["PROGRESS"] = "Explore Veithhól"; 
      ["LORE"] = "The rocky slopes of Veithhól serve as a den for the hunting-cats that stalk Misthallow. What few travellers find themselves in this area report the sound of soft, padding feet somewhere in the fog. The natural consequence of these reports seems obvious to the wiser among us: the cats must not have been hungry, or these reports would not exist."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "30.52N"; 
          ["LONG"] = "58.31W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Scraper's Clough"; 
      ["PROGRESS"] = "Explore Scraper's Clough"; 
      ["LORE"] = "The narrow clefts of Scraper's Clough have been expanded by the tireless work of the cave-claws that live here, an industrious effort that is halted only when it is time for the creatures to feed. This they do on the hapless beasts that stumble into the Clough, and on adventurers who do the same. These latter meals tend to underestimate the creatures as 'mere cave-claws,' a misjudgment the inhabitants of Scraper's Clough are only too pleased to encourage."; 
      ["POINTS"] = {
        [1] = {
