  -- Elderslade / Dwarf-ruins of Elderslade
  [1879411148] = {
      [1] = {
      ["NAME"] = "Hárveg Bridge"; 
      ["PROGRESS"] = "Discover the Hárveg Bridge"; 
      ["LORE"] = "Perched above the Greylin River, the Hárveg Bridge had been, until recently, broken and impassable. The dwarves have skilfully mended the bridge, opening safe passage to and from Misthallow once more."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "32.63N"; 
          ["LONG"] = "59.71W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Annâk-khurfu"; 
      ["PROGRESS"] = "Discover Annâk-khurfu"; 
      ["LORE"] = "Despite the many long years it has endured, Annâk-khurfu continues to stand tall. The dwarves have rallied to these ruins and called their allies to arms, preparing for the inevitable.\n\nAnnâk-khurfu offers an advantageous view of the main roads leading into Elderslade -- from Ered Mithrin to the east, and Misthallow to the south -- where friend and foe can be spotted plainly."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "32.74N"; 
          ["LONG"] = "61.12W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Zarkul-sulûn"; 
      ["PROGRESS"] = "Discover Zarkul-sulûn"; 
      ["LORE"] = "The years have not been kind to Zarkul-sulûn, which now plays host to all manner of evil: Orcs, hobgoblins, wargs, and perhaps others.\n\nThe ruins sit too closely to Annâk-khurfu for the dwarves' comfort. They keep a wary eye on Zarkul-sulûn, understanding that it will be the first of many obstacles they must cross if they wish to retake Gundabad."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "32.78N"; 
          ["LONG"] = "62.77W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Shakal-atrâd"; 
      ["PROGRESS"] = "Discover Shakal-atrâd"; 
      ["LORE"] = "The forces of Gundabad have long held Shakal-atrâd within their grasp. Though all appears quiet outside these foreboding ruins, delving deeper may reveal otherwise...."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "34.11N"; 
          ["LONG"] = "62.50W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Zudramdân"; 
      ["PROGRESS"] = "Discover Zudramdân"; 
      ["LORE"] = "Zudramdân may not be as formidable as Annâk-khurfu, but the dwarves' northern camp allows them some modicum of safety in the long fight to retake their home."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "36.80N"; 
          ["LONG"] = "64.65W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Grálinvarth"; 
      ["PROGRESS"] = "Discover Grálinvarth"; 
      ["LORE"] = "The rushing waterfalls of the Greylin River drown out even the loudest of sounds at Grálinvarth. From this vantage point, one can easily spot the Rushgate and Annâk-khurfu, as well as glimpse the valley of Misthallow on a clear day."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "35.32N"; 
          ["LONG"] = "58.45W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Gates of Gundabad"; 
      ["PROGRESS"] = "Discover the Gates of Gundabad"; 
      ["LORE"] = "Crude constructions deface the Gates of Gundabad, a mocking reminder of the dwarves' losses and defeats. One might wonder what else the Orcs have done to further corrupt the former dwarf-kingdom past this foreboding threshold."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "38.07N"; 
          ["LONG"] = "68.43W"; 
        }; 
      }; 
      };
  };
