  -- Elderslade / Enemy Encampments of Elderslade
  [1879411174] = {
      [1] = {
      ["NAME"] = "Howlers' Notch"; 
      ["PROGRESS"] = "Discover Howlers' Notch"; 
      ["LORE"] = "The rocky outcrops and hidden caves of the hills of Elderslade make for a perfect location for Warg dens. The ground is littered with bones and the weapons of those who tried and failed against the beasts.\n\nA foul odour permeates the air, and the rumble of low growls can be heard in the distance."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "35.75N"; 
          ["LONG"] = "62.58W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Malkorlash"; 
      ["PROGRESS"] = "Discover Malkorlash"; 
      ["LORE"] = "Squatting amidst sun-bleached bones of ancient worms, Malkorlash grants the hobgoblins control of both the nearby river and roads. The encampment also serves to hamper passage through the hills to the Wyrmsgráf River on the eastern side. The hobgoblin and dragon-kin filled passage is from whence Malkorlash, 'mountain gap,' gains its name."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "40.79N"; 
          ["LONG"] = "62.76W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Hanksar-mokh"; 
      ["PROGRESS"] = "Discover Hanksar-mokh"; 
      ["LORE"] = "Skulking in the shadows of Gundabad's great walls, Hanksar-mokh is a vile nest of Orcs. About the bone piles and hide tents, ancient dwarf-made structures lie in ruins, giving a hint of former glory. Rough scaffolding affords the goblin-kind the ability to swarm up and down the walls without having to use Gundabad's huge staircases."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "37.59N"; 
          ["LONG"] = "68.29W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Bagûd-mekhem"; 
      ["PROGRESS"] = "Discover Bagûd-mekhem"; 
      ["LORE"] = "The air about Bagûd-mekhem is unnaturally cold, with whipping winds cutting through all but the thickest furs and leathers. Ramshackle scaffolding and rough high walls have been erected about the northern walls of Gundabad. Orcs, goblins, and things even more feared swarm between walls of wood and stone."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "39.68N"; 
          ["LONG"] = "66.37W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Dûn Traikh"; 
      ["PROGRESS"] = "Discover Dûn Traikh"; 
      ["LORE"] = "Dûn Traikh bears testament to how far the Angmarim have pushed into Elderslade, and for how long they have enjoyed free reign in the lands of the dwarves. Though their realm has fallen, there are those within the walls of Dûn Traikh who strive to bring back the kingdom the Witch King of Angmar once begot."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "36.19N"; 
          ["LONG"] = "68.10W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Caivád Sâr"; 
      ["PROGRESS"] = "Discover Caivád Sâr"; 
      ["LORE"] = "Dark and looming, the spire of Caivád Sâr is where the Angmarim come to engage in ritual, arcane arts, and torture. Caivád Sâr provides a line of defence along the eastern road, housing numerous deadly forces. Its tower thrusts up to the heavens, a grasping claw from which enemies can be spotted miles away."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "38.51N"; 
          ["LONG"] = "64.65W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Kulûg-mokh"; 
      ["PROGRESS"] = "Discover Kulûg-mokh"; 
      ["LORE"] = "Skulking in the shadows of Gundabad’s great walls and twin to Hanksar-mokh, Kulûg-mokh too is a vile nest of Orcs. About the bone piles and hide tents, ancient dwarf-made structures lie in ruins, giving a hint of former glory. Rough scaffolding affords the goblin-kind the ability to swarm up and down the walls without having to use Gundabad's huge staircases."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "38.32N"; 
          ["LONG"] = "68.04W"; 
        }; 
      }; 
      };
  };
