  -- Bree-land / Flowers of the Wildwood
  [1879414498] = {
      [1] = {
      ["NAME"] = "Eyebright"; 
      ["PROGRESS"] = "Find eyebright"; 
      ["LORE"] = "This lovely little plant is said to be able to do everything from spreading cheer to curing ailments of the eyes. Some whisper that a tonic of eyebright dripped into the eye before bed will grant the sleeper prophetic dreams."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "22.55S"; 
          ["LONG"] = "58.09W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Ragged Robin"; 
      ["PROGRESS"] = "Find ragged robin"; 
      ["LORE"] = "Legends say that someone wearing this scraggly pink flower on their lapel will soon find love. They are more likely to be swarmed by butterflies, who have a deep fondness for ragged robin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.63S"; 
          ["LONG"] = "63.52W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Teasel"; 
      ["PROGRESS"] = "Find teasel"; 
      ["LORE"] = "Sought after by clothiers, the spines of the teasel can be used to raise a fuzzy nap on fabrics, especially wool. Water that collects in small cups which form around the stem is said to reverse signs of aging when applied to one's skin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.78S"; 
          ["LONG"] = "60.05W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Speedwell"; 
      ["PROGRESS"] = "Find speedwell"; 
      ["LORE"] = "Small and delicate, speedwell makes a fine tea, but does not make one move more quickly, as rumours often suggest. It may benefit one who suffers from leprosy or gout, however."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.38S"; 
          ["LONG"] = "56.17W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Shepherd's Purse"; 
      ["PROGRESS"] = "Find shepherd's purse"; 
      ["LORE"] = "A versatile plant that has both medicinal and culinary uses, Shepherd's Purse is often mistaken for a weed. Though its healing properties span numerous ailments, Shepherd's Purse is also thought to be beneficial in addressing a great number of blood-related issues."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.66S"; 
          ["LONG"] = "57.58W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Wood Spurge"; 
      ["PROGRESS"] = "Find wood spurge"; 
      ["LORE"] = "Leathery, dark green leaves and bright yellow flowers make wood spurge a lovely ground cover. When cut, however, the plant bleeds a milky white liquid that may cause pain and irritation of the skin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.18S"; 
          ["LONG"] = "55.40W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Yellow-rattle"; 
      ["PROGRESS"] = "Find yellow-rattle"; 
      ["LORE"] = "Towards the end of summer, the seed pods of the yellow-rattle turn to paper thin, dry husks. The sound made when the wind blows and rattles the seed within is how this lively plant got its name."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "17.98S"; 
          ["LONG"] = "60.04W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Knotted Thistle"; 
      ["PROGRESS"] = "Find knotted thistle"; 
      ["LORE"] = "Often looked upon with grimaces or trod underfoot, behind the needle-like bristles, the stem of the knotted thistle can be boiled down and used in pie like rhubarb. The flowers, however, have a history of being used to curdle milk by jealous neighbours."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.94S"; 
          ["LONG"] = "58.73W"; 
        }; 
      }; 
      };
  };
