  -- Bree-land / Sites of the Wildwood
  [1879414505] = {
      [1] = {
      ["NAME"] = "Trader's Wharf"; 
      ["PROGRESS"] = "Explore Trader's Wharf"; 
      ["LORE"] = "Trader's Wharf is the home base of the League of the Axe and one of the few access points into the Wildwood. Merchants, hunters, miners, and others pass through this small port on their way to and from gathering resources in the forests."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.31S"; 
          ["LONG"] = "61.45W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Writhenset"; 
      ["PROGRESS"] = "Explore Writhenset"; 
      ["LORE"] = "The twisting tunnels of Writhenset were carved into the hills long ago, by unknown miners. Left abandoned for centuries, the mines have become a contested battleground among those who seek their wealth and secrets."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.81S"; 
          ["LONG"] = "53.09E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "East-delf"; 
      ["PROGRESS"] = "Explore the East-delf"; 
      ["LORE"] = "Huge wooden beams line the rock walls leading to the eastern entrance of the Writhenset mines."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.90S"; 
          ["LONG"] = "60.79W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "West-delf"; 
      ["PROGRESS"] = "Explore the West-delf"; 
      ["LORE"] = "Wooden supports and scaffolding line the walls of the western entrance to Writhenset mines. The pit has been carved out of the mountain over unknown generations, but the mines go deeper still."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.88S"; 
          ["LONG"] = "61.91W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Marl's Crossing"; 
      ["PROGRESS"] = "Explore Marl's Crossing"; 
      ["LORE"] = "High above the rapids and rushing water of the Netherwell, a makeshift bridge sways in the wind. Marl the Rockscrambler was the first man in known history to have scaled the walls of the Cirith Núr chasm. Sadly, whatever beast was lying in wait on the side of the Wildwood made short work of the adventurer."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.83S"; 
          ["LONG"] = "60.19W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Hunter's Ledge"; 
      ["PROGRESS"] = "Explore Hunter's Ledge"; 
      ["LORE"] = "This rocky prominence above the central Wildwood grants an unobstructed view of the surrounding valley. Given the dangerous beasts that frequent this area, it is unclear who is the hunter and who is the prey."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.33S"; 
          ["LONG"] = "57.61W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Helva's Edge"; 
      ["PROGRESS"] = "Explore Helva's Edge"; 
      ["LORE"] = "In the days before Cotfast was overrun, one of the village's children wandered away from her parents. Helva was found a day later, her back to this cliff, with wolves closing in. A brilliant, snow-white stag stood tall before her, keeping the wolves at bay. The arrival of the villagers meant the wolves were outnumbered, and they dispersed. The stag gently touched his nose to the top of the child's head before vanishing over the ledge."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.60S"; 
          ["LONG"] = "58.27W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Blomley Sward"; 
      ["PROGRESS"] = "Explore Blomley Sward"; 
      ["LORE"] = "Blomley Sward is a beautiful meadow, carpeted with thick green grass and countless white flowers. It would be the perfect place for a hobbit's picnic, were it not for the beasts that prowl the hillside and nearby woods."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "22.54S"; 
          ["LONG"] = "60.84W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "The Old Bluff-house"; 
      ["PROGRESS"] = "Explore the Old Bluff-house"; 
      ["LORE"] = "Nature has reclaimed the ruined walls and shattered dome of this ancient gazebo. The view from the bluff encompasses much of the Wildwood, but the Old Bluff-house does not seem to have been a watch-tower. Instead, the structure harkens back to a time where building for beauty's sake was possible."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.50S"; 
          ["LONG"] = "59.24W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Overwine Hall"; 
      ["PROGRESS"] = "Explore Overwine Hall"; 
      ["LORE"] = "This ancient Arnorian ruin once stood as a sentinel, keeping watch over the Brandywine River. These days, the crumbling walls play host to dark forces who use Overwine Hall as a base from which to set forth to ravage and raid the Wildwood."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.72S"; 
          ["LONG"] = "59.50W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Norwarden"; 
      ["PROGRESS"] = "Explore Norwarden"; 
      ["LORE"] = "The tall and thin tower of Norwarden is now home to those for whom it was built to guard against. The years have not been kind to the watch-tower's old stonework, nor have its newer residents."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.47S"; 
          ["LONG"] = "55.21W"; 
        }; 
      }; 
      };
      [12] = {
      ["NAME"] = "Sutwarden"; 
      ["PROGRESS"] = "Explore Sutwarden"; 
      ["LORE"] = "Carved from the very bedrock of the mountain itself, Sutwarden was once a majestic Arnorian watch-tower. It has since fallen to ruin, and is further defiled by its current occupants."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.57S"; 
          ["LONG"] = "56.01W"; 
        }; 
      }; 
      };
      [13] = {
      ["NAME"] = "Cotfast"; 
      ["PROGRESS"] = "Explore Cotfast"; 
      ["LORE"] = "The small village of Cotfast was originally built by the Free Peoples. The villagers were forced to abandon it due to a bad harvest and the growing threats and dangers of the Wildwood. Now it is home to brigands and Orcs alike."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.91S"; 
          ["LONG"] = "56.84W"; 
        }; 
      }; 
      };
      [14] = {
      ["NAME"] = "Gnawbone Pit"; 
      ["PROGRESS"] = "Explore Gnawbone Pit"; 
      ["LORE"] = "Sun bleached bones and rusted swords litter the hillside of Gnawbone Pit. The Wargs who den among the rocks have grown strong and fierce on the bounty of the Wildwood."; 
      ["POINTS"] = {
