  -- Blood of Azog / Remnants of Khazad-dûm
  [1879415678] = {
      [1] = {
      ["NAME"] = "Amdân, the Threshold"; 
      ["PROGRESS"] = "Learn of Amdân"; 
      ["LORE"] = "'Before the fall of Moria, Amdân was once a thriving trading village that sprung forth beneath the great archway Durin himself had set at the southern border of the realm of Khazad-dûm. It has now become the mustering camp of the Haban’akkâ.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "63.78N"; 
          ["LONG"] = "134.96W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Gabilthurkhu, the Great-way"; 
      ["PROGRESS"] = "Learn of Gabilthurkhu"; 
      ["LORE"] = "'Once a mighty thoroughfare and trading route leading out of Khazad-dûm through Amdân and into the Misty Mountains, the great-way had mostly vanished after the fall of Moria, leaving only stray stone and markers in its wake.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.95N"; 
          ["LONG"] = "134.56W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Labam-gairu, the Whitcleft"; 
      ["PROGRESS"] = "Learn of Labam-gairu"; 
      ["LORE"] = "'In the dark of winter, Labam-gairu, or the Whitcleft in the Common tongue, had all but frozen solid. Cold water still flowed from its eastern falls as it neared the lands of the Elves, but this tale does not speak of their realm.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.03N"; 
          ["LONG"] = "135.66W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Khur Azan, the Dim-crossing"; 
      ["PROGRESS"] = "Learn of Khur Azan"; 
      ["LORE"] = "'At Khur Azan, the paths to Khazad-dûm and Lórien met. In by-gone times, a stone pavilion was wrought in commemoration of friendship between dwarves and Elves. The pavilion of Khur Azan still stands, but the realm of the Elves now seems cold and distant even beneath its brilliant, golden eaves.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.45N"; 
          ["LONG"] = "133.01W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Kibil-nâla, the Silverlode"; 
      ["PROGRESS"] = "Learn of Kibil-nâla"; 
      ["LORE"] = "'Ah, the Silverlode! As brilliant and chilling as ever it had been! Falling from Kheled-zâram, its cool mist soothed the wounds of war.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "64.82N"; 
          ["LONG"] = "133.08W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Tharkh Shathur"; 
      ["PROGRESS"] = "Learn of Tharkh Shathur"; 
      ["LORE"] = "'Once the pass to Atrad-zarakh, an avalanche on the slopes of Shathûr left it inaccessible for many years after the fall of Moria. In constructing one of their crude bridges to reach the old pass, it seems the Orcs have unwittingly done us a great favour!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "68.17N"; 
          ["LONG"] = "132.19W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Ashmargathâl"; 
      ["PROGRESS"] = "Learn of Ashmargathâl"; 
      ["LORE"] = "'With the aid of the Kámbrada, we won a foothold on the slopes of Bundushathûr. The great guard-post of Ashmargathâl was had fallen to the Orcs, but no longer. Alas, the watching-spire beyond would be harder won.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.52N"; 
          ["LONG"] = "133.25W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Atrad-zarakh, the Warding-spire"; 
      ["PROGRESS"] = "Learn of Atrad-zarakh"; 
      ["LORE"] = "'It was at Atrad-zarakh, the watching-spire, that the great bells of Khazad-dûm once rang across Azanulbizar. Once sounded in times of celebration, they became a warning-beacon in the latter days of Moria. Alas, now the tower is overrun.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.36N"; 
          ["LONG"] = "134.68W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Tharkh Baraz"; 
      ["PROGRESS"] = "Learn of Tharkh Baraz"; 
      ["LORE"] = "'Of all the passes among the peaks of Khazad-dûm, the most-travelled in times of old was Tharkh Baraz — the Redhorn Pass. It is my hope that in years to come it might prove safe to walk again.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.37N"; 
          ["LONG"] = "137.47W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Anlakh-zeleg"; 
      ["PROGRESS"] = "Learn of Anlakh-zeleg"; 
      ["LORE"] = "'Even the corruption of the Orcs could not dim the beauty of Anlakh-zeleg, the Dimrill Stair! At the foot of Tharkh Baraz, its falls shimmered in sunlight as they cascaded down into Kheled-zâram.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.87N"; 
          ["LONG"] = "136.15W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Tharkh Zigil"; 
      ["PROGRESS"] = "Learn of Tharkh Zigil"; 
      ["LORE"] = "'In truth, little is known of Tharkh Zigil. Once a pass carved into the slopes of Zirakzigil, it fell into disuse even before the fall of Moria. Perhaps it was once meant to lead to Durin's Tower, but I have not discovered its true purpose.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.81N"; 
          ["LONG"] = "137.47W"; 
        }; 
      }; 
      };
      [12] = {
      ["NAME"] = "Zirakazhâr"; 
      ["PROGRESS"] = "Learn of Zirakazhâr"; 
      ["LORE"] = "'Once an armoury and southern watch-post of Khazad-dûm, Zirakazhâr was overrun by Orcs not long after the fall of Moria. If we are to overcome Azog's forces, it must be reclaimed!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.22N"; 
          ["LONG"] = "136.70W"; 
        }; 
      }; 
      };
      [13] = {
      ["NAME"] = "Kheled-zâram, the Mirrormere"; 
      ["PROGRESS"] = "Learn of Kheled-zâram"; 
      ["LORE"] = "'Ah, the Mirrormere! There must be some enchantment upon it, for always it reflects the night sky. It was in the waters of Kheled-zâram that Durin the Deathless first glimpsed a crown of stars upon his head, and in his wisdom, he delved the first halls of Khazad-dûm.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "67.25N"; 
          ["LONG"] = "134.62W"; 
        }; 
      }; 
      };
      [14] = {
      ["NAME"] = "Durinul’aban"; 
      ["PROGRESS"] = "Learn of Durinul’aban"; 
      ["LORE"] = "'It has been many years since any dwarf has looked upon Durin'abal. Durin's Stone was set in commemoration of where Durin the Deathless stood when first he gazed into Kheled-zâram. It brings me great comfort to see it, even in the midst of war.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.50N"; 
          ["LONG"] = "134.98W"; 
        }; 
      }; 
      };
      [15] = {
      ["NAME"] = "Nud-mekhem, the East-gate"; 
      ["PROGRESS"] = "Learn of Nud-mekhem"; 
      ["LORE"] = "'After so many years, the facade of Nud-mekhem, the great East-gate, has resisted falling to complete ruin despite the efforts of the Orcs. The gates are defaced with Orkish scrawls, but as with all works of the Orcs and their ilk, it cannot endure!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "67.60N"; 
          ["LONG"] = "137.52W"; 
        }; 
      }; 
      };
  };
