  -- Blood of Azog / Azog's Realm
  [1879415681] = {
      [1] = {
      ["NAME"] = "Kamaz-kala"; 
      ["PROGRESS"] = "Learn of Kamaz-kala"; 
      ["LORE"] = "'Sullying the waters of Kibil-nâla with their filth, the Orcs had constructed a crude border-fort along the great-way of old, blocking our surest path to the East-gate.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "45.67N"; 
          ["LONG"] = "123.93W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Gadh Ujáf"; 
      ["PROGRESS"] = "Learn of Gadh Ujáf"; 
      ["LORE"] = "'In the low-hills of Zirakzigil, I found a great den of wargs. Before Komog, youngest son of Azog, was slain at Gundabad, his wargs had claimed the lives of countless dwarves. At last, vengeance would be ours!'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "65.22N"; 
          ["LONG"] = "135.42W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Dursh Hûl"; 
      ["PROGRESS"] = "Learn of Dursh Hûl"; 
      ["LORE"] = "'To the north of Khur Azan, we found the old bridge destroyed. In its place, the Orcs had erected a small crossing to guard the pass to Shathûr.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "65.87N"; 
          ["LONG"] = "132.67W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Târ-kútoz"; 
      ["PROGRESS"] = "Learn of Târ-kútoz"; 
      ["LORE"] = "'In the shadow of Atrad-zarakh on the slopes of Shathûr, the Orcs had built a sprawling watch-post. Nestled among their crude trappings were countless bats, creatures long-favoured by Azog and his sons to use as spies at Azanulbizar.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.80N"; 
          ["LONG"] = "134.65W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Rung-shakam"; 
      ["PROGRESS"] = "Learn of Rung-shakam"; 
      ["LORE"] = "'Far above the East-gate on the slopes of Zirakzigil, the Orcs made camp with trolls out of Moria. From those dizzying heights, the trolls hurled countless stones down into the dale, killing Orcs and dwarves alike.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "68.12N"; 
          ["LONG"] = "137.67W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Zaghúth"; 
      ["PROGRESS"] = "Learn of Zaghúth"; 
      ["LORE"] = "'When at last we came to Azanulbizar, we found the pass through Baraz blocked by a great Orc-encampment. There would be no reinforcements from the north - at least, none that would meet us as friends and allies.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "70.88N"; 
          ["LONG"] = "137.32W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Muru Azog"; 
      ["PROGRESS"] = "Learn of Muru Azog"; 
      ["LORE"] = "'Before the gates of Moria, Azog and his Orcs wrought a great battlement. In the shadows of his watch-towers, the skulls of countless dwarves had been set on pikes in mockery of Thráin.'"; 
