  -- Gundabad / Scouting the Sorrowglen
  [1879422516] = {
      [1] = {
      ["NAME"] = "Leitstáth"; 
      ["PROGRESS"] = "Explore Leitstáth"; 
      ["LORE"] = "After overcoming the forces of the Iron Crown at Rathad Caul, the Narrow Way, an expedition of Zhélruka under orders from Prince Ingór established the camp of Leitstáth in Câr Bronach. After the capture of Grúmachath, however, Leitstáth now primarily serves as a staging ground and supply post for the Gabil'akkâ."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "46.97N"; 
          ["LONG"] = "64.95W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Crágan Bris"; 
      ["PROGRESS"] = "Explore Crágan Bris"; 
      ["LORE"] = "Destroyed when the Witch-king of Angmar sought dominion over Câr Bronach, Crágan Bris was once a village of stone-giants of the Grímklettar clan. After the Grímklettar were displaced from their ancestral lands by the servants of Angmar, it was many years before any dared to return to Crágan Bris. Alas, naught but broken stones remained."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "49.54N"; 
          ["LONG"] = "65.50W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Grúmachath"; 
      ["PROGRESS"] = "Explore Grúmachath"; 
      ["LORE"] = "Built long ago when the kingdom of Angmar still spanned lands as far to the east as Câr Bronach, Grúmachath was once a fortified outpost of the Iron Crown. Captured in recent days by the Zhélruka expedition at the behest of Prince Ingór, Grúmachath now serves as the main encampment of the Dwarves of the Gabil'akkâ in Câr Bronach as they work to reclaim Mount Gundabad."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "50.15N"; 
          ["LONG"] = "68.54W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Eiglinn"; 
      ["PROGRESS"] = "Explore Eiglinn"; 
      ["LORE"] = "Once a shimmering lake in the western heights of Ered Mithrin, Eiglinn has been defiled by the Angmarim of the Iron Crown. An unexpected by-product of fell sorcery and experimentation at Tûr Fúar, the Drearspire, a harsh, blueish-green runoff, known to the Zhélruka as spell-dross, has seeped into the groundwater, polluting Eiglinn and other bodies of water in Câr Bronach."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "49.73N"; 
          ["LONG"] = "70.61W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Clavanail"; 
      ["PROGRESS"] = "Explore Clavanail"; 
      ["LORE"] = "Driven from their homes in the glen after the Iron Crown fled Angmar, many of the birds of Câr Bronach now nest in the rocky heights of Clavanail, save, of course, for carrion-birds, who now feast upon the remains of the victims of the Iron Crown."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.19N"; 
          ["LONG"] = "72.14W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Eiricháv"; 
      ["PROGRESS"] = "Explore Eiricháv"; 
      ["LORE"] = "After the fall of Angmar, dissent grew within the Hill-men of the Trév Duvárdain. Although many of their kin still serve the Iron Crown, a growing number of Hill-men have abandoned their wicked brethren and taken refuge at Eiricháv. Whether they will return to Angmar and unify with the Trév Gállorg remains uncertain, but at Eiricháv, they live free of their oppressors--and that is enough."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.08N"; 
          ["LONG"] = "71.40W"; 
        }; 
      }; 
      };
  };
  -- Gundabad / The Last Frontier of Angmar
  [1879422517] = {
      [1] = {
      ["NAME"] = "Rathad Caul, the Narrow Way"; 
      ["PROGRESS"] = "Explore Rathad Caul, the Narrow Way"; 
      ["LORE"] = "Between the lands of Elderslade and Câr Bronach lies a fortified passage known as Rathad Caul, the Narrow Way.  Surrounded by sheer cliffs, Rathad Caul was long ago breached and abandoned after Angmar fought several bloody battles against the Zhélruka. As it was part of Angmar's vast border during the reign of the Witch-king, Rathad Caul also marks the westernmost point of Men Angrún, an ancient road spanning from Angmar to Rhûn which once issued out of Câr Bronach."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "44.57N"; 
          ["LONG"] = "63.88W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Hlídborg"; 
      ["PROGRESS"] = "Explore Hlídborg"; 
      ["LORE"] = "After the stone-giants of the Grímklettar clan were displaced from their ancestral home at Tind Brotna by the Witch-king of Angmar and the settlement of Crágan Bris was destroyed, they withdrew across the valleys of Câr Bronach. Upon a sheer, southern slope, the Grímklettar founded a new settlement: Hlídborg. Following centuries of hardship, some of the Grímklettar have deigned to treat with the Angmarim of the Iron Crown in hopes of changing their fortunes, but this has served only to further splinter the remnants of their clan."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "48.95N"; 
          ["LONG"] = "63.68W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Skáilag"; 
      ["PROGRESS"] = "Explore Skáilag"; 
      ["LORE"] = "When the realm of Angmar was at the height of its power, wicked Hill-men of Rhudaur fought and died in service of the Witch-king. Those who fell were entombed in vast barrows delved into the steep-walled valleys of Câr Bronach. Although many of these barrows are now lost, one remains: Skáilag."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "50.89N"; 
          ["LONG"] = "65.88W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tâg Fógarrach"; 
      ["PROGRESS"] = "Explore Tâg Fógarrach"; 
      ["LORE"] = "Despite swearing fealty to the Iron Crown, the Hill-men of the Trév Duvárdain splintered in the aftermath of the failed rebirth of Angmar. Many of them fled Carn Dûm, seeking shelter in the hills of Angmar or begging the mercy of the Trév Gállorg to dwell within Aughaire. However, some of the Trév Duvárdain remained loyal servants of the Iron Crown and followed their defeated masters to Câr Bronach in the hope that the glory promised them might still be won. At Tâg Fógarrach, the Trév Duvárdain have made camp in the ruins of an old Angmarim settlement, seemingly undaunted by the fate of the Men who dwelt there before them."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "51.33N"; 
          ["LONG"] = "71.61W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Toll Oivas"; 
      ["PROGRESS"] = "Explore Toll Oivas"; 
      ["LORE"] = "Once merely a winding cavern inhabited by the bats of Mount Gundabad, Toll Oivas has become a site of dreadful sorcery since the Angmarim's return to Câr Bronach. Determined to know the plans of the Orcs, Frost-horde, and even the Gabil'akkâ, the Angmarim have begun to bind fell spirits to the remains of slain bats that they might set them aflight as spies both within and without Mount Gundabad."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "51.79N"; 
          ["LONG"] = "74.91W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Mur Shatraug, the Witch-gate"; 
      ["PROGRESS"] = "Explore Mur Shatraug, the Witch-gate"; 
      ["LORE"] = "Leading into Câr Bronach from the heights of the Pit of Stonejaws, Mur Shatraug, the Witch-gate, as it has come to be known, was once the east-gate of Mount Gundabad. Long after the fall of Mount Gundabad and its occupation by the Orcs, the east-gate was claimed by the dread realm of Angmar and reshaped into a small settlement. Although Angmar fell in the years that followed, Mur Shatraug still stands as a monument to the power the Witch-king of Angmar once held in Middle-earth."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "48.42N"; 
          ["LONG"] = "70.62W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Agal Dûn"; 
      ["PROGRESS"] = "Explore Agal Dûn"; 
      ["LORE"] = "Wrought during Angmar's height, Agal Dûn, the fortress of horror, has long stood watch over Câr Bronach. Known to some only by the name of its broken tower, Tûr Fúar, the Drearspire, Agal Dûn was the easternmost settlement of Angmar until it was abandoned following a series of battles between the Zhélruka and the forces of the Witch-king in the years that preceded the realm's downfall. In the centuries that followed, it lay dormant and largely forgotten until the False King of Angmar, Mordirith, was deposed and the Angmarim of the Iron Crown were forced to flee in defeat after the failed rebirth of their realm."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "52.58N"; 
          ["LONG"] = "69.29W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Tûr Fúar, the Drearspire"; 
      ["PROGRESS"] = "Explore Tûr Fúar, the Drearspire"; 
      ["LORE"] = "Sundered in battles long ago between Angmar and the Zhélruka, Tûr Fúar, the Drearspire looms over Câr Bronach like a twisted, skeletal talon. At the peak of Angmar's might, it was rumoured that the Witch-king of Angmar kept a powerful artifact within Tûr Fúar, but after the downfall of Angmar, Tûr Fúar stood all but empty for over one thousand years. Throughout its long dormancy, the Orcs avoided and scorned it, and thinking it cursed, they renamed it Shalgaush: the Bonefinger. Indeed, even during the ill-fated rebirth of Angmar, neither the False King of Angmar nor his servants returned to lay claim to the Drearspire. Defeated and driven from Angmar, the remnants of the Iron Crown have taken refuge within Tûr Fúar, where they desperately seek for a means to regain power and restore the realm of Angmar to its former glory."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "57.72N"; 
          ["LONG"] = "10.21W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Bálach Iaran, the Iron Pass"; 
      ["PROGRESS"] = "Explore Bálach Iaran, the Iron Pass"; 
      ["LORE"] = "Twisting through the mountains from Nan Gurth to Câr Bronach, Bálach Iaran, the Iron Pass, lay barren and untravelled for many years after the fall of Angmar. After the defeat of the False King of Angmar at Barad Gúlaran, however, it was through Bálach Iaran that the Iron Crown fled to Câr Bronach."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "54.41N"; 
          ["LONG"] = "72.94W"; 
        }; 
      }; 
      };
  };
