  -- Gundabad / The Cradle of Durin
  [1879422591] = {
      [1] = {
      ["NAME"] = "Zâram-mekhem"; 
      ["PROGRESS"] = "Explore Zâram-mekhem"; 
      ["LORE"] = "Bearing dire warnings in Khuzdul, Zâram-mekhem, the tarn-gate, stands in the form of two great rune-inscribed columns of stone where Deepscrave meets Gloomingtarn. Once possessed of a powerful enchantment which forbade all but the Dwarves of Middle-earth from entering the hallowed caverns of Mount Gundabad, it was overcome in recent days by Angmarim who wielded an artifact crafted long ago by the Witch-king of Angmar: Máltagar, the Fell Graver."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "50.43S"; 
          ["LONG"] = "120.47W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Durthuzâram, the Dark Lake"; 
      ["PROGRESS"] = "Explore Durthuzâram, the Dark Lake"; 
      ["LORE"] = "Durthuzâram, the dark lake, is one of the natural wonders of Mount Gundabad and, indeed, of Middle-earth. Deep, clear, and as still as a tranquil sea, it was Durthuzâram, the Gloomingtarn, which lent its name to the hallowed caverns of Mount Gundabad."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "46.28S"; 
          ["LONG"] = "120.85W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Watchers' Roost"; 
      ["PROGRESS"] = "Explore Watchers' Roost"; 
      ["LORE"] = "Once a great overlook by which the Longbeards could gaze across nearly the entirety of Durthuzâram, the Watchers' Roost has become a makeshift encampment for the Gabil'akkâ as they work to repel their foes from the hallowed caverns of Gloomingtarn."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "47.72S"; 
          ["LONG"] = "121.91W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Lugun-nâla"; 
      ["PROGRESS"] = "Explore Lugun-nâla"; 
      ["LORE"] = "Lugun-nâla, the sacred path, is an ancient bridge wrought of the basalt of Gloomingtarn. Arching over Durthuzâram, Lugun-nâla leads to perhaps the most sacred site in all of Mount Gundabad: Abkân'aban, the Stone of Wakening."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "45.40S"; 
          ["LONG"] = "122.12W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Abkân'aban, the Stone of Wakening"; 
      ["PROGRESS"] = "Explore Abkân'aban, the Stone of Wakening"; 
      ["LORE"] = "According to legend, it was at Abkân'aban, the Stone of Wakening, where Durin the Deathless first awoke in the depths of Mount Gundabad. Atop his earthen cradle, Durin himself is purported to have wrought an anvil of black stone, Narag-dehar, and a forge of white brick, Labam-khebab. After the Greymaul Rebellion, however, it was decreed that the forge should remain dark until the prophesied return of Durin the Last, and so it was extinguished, serving ever after only as a site of ceremony for the Longbeards."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "44.99S"; 
          ["LONG"] = "120.87W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Burmumekhem"; 
      ["PROGRESS"] = "Explore Burmumekhem"; 
      ["LORE"] = "Bearing dire warnings in Khuzdul, Burmumekhem, the gap-gate, stands in the form of two great rune-inscribed columns of stone where Clovengap meets Gloomingtarn. Once possessed of a powerful enchantment which forbade all but the Dwarves of Middle-earth from entering the hallowed caverns of Mount Gundabad, it too was overcome by the Angmarim of the Iron Crown."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "42.63S"; 
          ["LONG"] = "121.90W"; 
        }; 
      }; 
      };
  };
