  [1879422593] = {
      [1] = {
      ["NAME"] = "Drúkala"; 
      ["PROGRESS"] = "Explore Drúkala"; 
      ["LORE"] = "Long known to the Orcs as Skaizagâf, or the curse-cave, Gloomingtarn was forbidden to all of their kind until the Angmarim overcame the warding-runes with the aid of Máltagar, the Fell Graver. Although the Orcs may now enter Gloomingtarn without fear of succumbing to the warding-runes' enchantment, many choose not to remain long, for the echoes in the dark seem possessed of a life of their own. Those few Orcs who dare to explore the hallowed cavern have made camp at Drúkala, not far beyond the first stones of the path where Deepscrave meets Gloomingtarn."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "50.20S"; 
          ["LONG"] = "121.29W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Ormgren"; 
      ["PROGRESS"] = "Explore Ormgren"; 
      ["LORE"] = "Although nearly no evidence remains of its once-rich deposits, Ormgren was among the first lodes delved by Durin the Deathless after he awoke in Mount Gundabad. For many years after Gloomingtarn was sealed, glow-worms were the only inhabitants of the old tunnels, but since the arrival of the Angmarim, many of their kind have grown unusually aggressive and territorial. However, others have merely withdrawn deeper into Ormgren, casting their pale light into long-forgotten chambers of Mount Gundabad."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "44.65N"; 
          ["LONG"] = "11.60E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Glittengrof"; 
      ["PROGRESS"] = "Explore Glittengrof"; 
      ["LORE"] = "Rumoured to be the cavern where Durin the Deathless discovered the first crystals he would use to light the halls of Mount Gundabad, Glittengrof is now home to an invasive brood of spiders whose webs threaten to defile the whole of Gloomingtarn."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.37N"; 
          ["LONG"] = "25.50E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Skelstrand"; 
      ["PROGRESS"] = "Explore Skelstrand"; 
      ["LORE"] = "Along the western bank of Durthuzâram lies Skelstrand, a stretch of jagged shoals and tidepools inhabited by the crab-like ukhrâsh."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "46.97S"; 
          ["LONG"] = "121.73W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Mîn Lorg"; 
      ["PROGRESS"] = "Explore Mîn Lorg"; 
      ["LORE"] = "Determined to uncover the secrets of Gloomingtarn, the Angmarim have unwittingly discovered Mîn Lorg, an ancient and seemingly-untouched passage which leads downward into the deepest depths of Mount Gundabad."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "45.65S"; 
          ["LONG"] = "125.73W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Lochan Dúv"; 
      ["PROGRESS"] = "Explore Lochan Dúv"; 
      ["LORE"] = "Established not long after the Angmarim breached the warding-runes of Gloomingtarn, Lochan Dúv is the Iron Crown's deepest foothold within the hallowed caverns of Mount Gundabad. Although many Angmarim have since travelled into the higher reaches of the Mountain-home, some have chosen to remain, hoping to uncover secrets and relics of the Elder Days that may still lie hidden in Gloomingtarn."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "45.77S"; 
          ["LONG"] = "123.69W"; 
        }; 
      }; 
      };
  };
